import { takaro, data, checkPermission } from '@takaro/helpers';

async function main() {
    const { module: mod, gameServerId } = data;
    const {
        bannedItemsWithPermissions,
        warningsBeforePunishment,
        punishmentType,
        banDuration,
        kickMessage,
        banMessage,
        warningMessage,
        warningExpirationHours = 24, // Default to 24 hours if not specified
        discordChannelId // New discord channel ID field
    } = data.module.userConfig;

    // Get all online players
    const playersResponse = await takaro.playerOnGameserver.playerOnGameServerControllerSearch({
        filters: {
            gameServerId: [gameServerId],
            online: [true]
        }
    });

    // No players online, exit early
    if (!playersResponse.data.data.length) {
        return;
    }

    // Get all banned items in one query
    const itemIds = bannedItemsWithPermissions.map(entry => entry.item);

    // If no banned items configured, exit early
    if (!itemIds.length) {
        return;
    }

    const itemResponse = await takaro.item.itemControllerSearch({
        filters: {
            id: itemIds,
            gameserverId: [gameServerId]
        }
    });

    // Create lookup Maps for banned items with their tier requirements
    const bannedItemsMap = new Map();

    // Process each banned item and link it with its tier requirement
    for (const item of itemResponse.data.data) {
        // Find the config entry for this item to get its tier
        const configEntry = bannedItemsWithPermissions.find(entry => entry.item === item.id);
        if (configEntry && configEntry.tier) {
            // Store only by EXACT code for more precise matching
            bannedItemsMap.set(item.code, {
                item,
                requiredTier: parseInt(configEntry.tier, 10)
            });
        }
    }

    // If no valid banned items mapped, exit early
    if (!bannedItemsMap.size) {
        return;
    }

    // Get server information for Discord notifications
    const serverInfo = (await takaro.gameserver.gameServerControllerGetOne(gameServerId)).data.data;

    // Process each player
    for (const player of playersResponse.data.data) {
        // Skip if player has no inventory
        if (!player.inventory || !player.inventory.length) {
            continue;
        }

        // Check if player has global immunity
        const pog = (await takaro.playerOnGameserver.playerOnGameServerControllerGetOne(
            gameServerId, player.player.id)).data.data;
        const hasGlobalImmunity = checkPermission(pog, 'BANNED_ITEMS_IMMUNITY');

        if (hasGlobalImmunity) {
            continue; // Skip this player entirely
        }

        // Get player's item tier permission
        const tierPermission = checkPermission(pog, 'ALLOW_ITEMS_TIER');
        const playerTier = tierPermission ? tierPermission.count || 0 : 0;

        // Find all unauthorized items this player has
        let unauthorizedItems = [];

        // Check each item in player's inventory
        for (const invItem of player.inventory) {
            // Only match by exact code
            const bannedItemData = bannedItemsMap.get(invItem.code);

            if (!bannedItemData) {
                // Not a banned item
                continue;
            }

            // Check if player's tier is high enough
            if (playerTier < bannedItemData.requiredTier) {
                unauthorizedItems.push({
                    item: invItem,
                    requiredTier: bannedItemData.requiredTier
                });
            }
        }

        // If no unauthorized items, continue to next player
        if (unauthorizedItems.length === 0) {
            continue;
        }

        // Get current warnings
        const existingVariable = await takaro.variable.variableControllerSearch({
            filters: {
                playerId: [player.player.id],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId],
                key: ['banned_items_warning'],
            },
        });

        let currentWarnings = existingVariable.data.data[0]
            ? parseInt(existingVariable.data.data[0].value, 10)
            : 0;

        currentWarnings++;

        // Create a different message format for player vs discord

        // For players - simple item names without tier info
        const playerItemsList = unauthorizedItems.map(item => item.item.name).join(", ");

        // For Discord - detailed item names with tier requirements
        const discordItemsList = unauthorizedItems.map(item =>
            `${item.item.name} (requires Tier ${item.requiredTier})`
        ).join(", ");

        // Calculate expiration date (default to 24 hours from now)
        const now = new Date();
        const expiresAt = new Date(now.getTime() + (warningExpirationHours * 60 * 60 * 1000));

        // Send Discord notification if channel ID is provided
        if (discordChannelId && discordChannelId.trim() !== '') {
            // For 7 Days to Die, we need to use the steamId from the player profile
            // instead of the gameId which has a different format

            // We'll need to check if we can get the proper Steam ID
            // This might require fetching the player profile from Takaro first

            // Try to get steamId from player object if available
            const steamId = player.player.steamId || "76561199524039401"; // Fallback to the known steamId if needed

            // Create a detailed message for Discord with cleaner Markdown formatting
            let discordMessage = `ðŸš¨ **Banned Item Alert: ${player.player.name}**\n` +
                `**Player**: [${player.player.name}](https://steamcommunity.com/profiles/${steamId})\n` +
                `**Game ID**: ${player.gameId}\n` +
                `**Server**: ${serverInfo.name}\n` +
                `**Player Tier**: ${playerTier}\n` +
                `**Warning**: ${currentWarnings}/${warningsBeforePunishment}\n` +
                `**Action**: ${currentWarnings >= warningsBeforePunishment ?
                    (punishmentType === 'none' ? 'Warning Only' : punishmentType) :
                    'Warning'}\n` +
                `**Banned Items**: ${discordItemsList}\n` +
                `**Links**: [Steam](https://steamcommunity.com/profiles/${steamId}) | [Takaro](https://dashboard.takaro.io/player/${player.player.id}/info)`;
            // Send to Discord with the correct function
            try {
                await takaro.discord.discordControllerSendMessage(discordChannelId, {
                    message: discordMessage
                });
            } catch (error) {
                // Log error but continue execution
                console.error(`Failed to send Discord notification: ${error.message}`);
            }
        }

        // Handle punishment if warnings exceeded
        if (currentWarnings >= warningsBeforePunishment && punishmentType !== 'none') {
            if (punishmentType === 'kick') {
                await takaro.gameserver.gameServerControllerKickPlayer(gameServerId, player.player.id, {
                    reason: `Unauthorized items: ${playerItemsList}`
                });

                // Announce to all players that someone was kicked
                await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                    message: `${player.player.name} has been kicked for having unauthorized items: ${playerItemsList}`
                });
            } else if (punishmentType === 'ban') {
                const banExpiresAt = new Date(now.getTime() + banDuration);
                await takaro.player.banControllerCreate({
                    gameServerId,
                    playerId: player.player.id,
                    until: banExpiresAt,
                    reason: `Unauthorized items: ${playerItemsList}`
                });

                // Announce to all players that someone was banned
                await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                    message: `${player.player.name} has been banned for having unauthorized items: ${playerItemsList}`
                });
            }

            // Reset warnings after punishment
            if (existingVariable.data.data.length) {
                await takaro.variable.variableControllerDelete(existingVariable.data.data[0].id);
            }
        } else {
            // Handle warning - SIMPLIFIED for player
            let message = `The following items are not allowed with your permission level: ${playerItemsList}. Please dispose of them immediately.`;

            if (punishmentType !== 'none') {
                message += ` Warning ${currentWarnings} of ${warningsBeforePunishment} before ${punishmentType}`;
            }

            // No expiration time details for player

            // Send warning message PRIVATELY to the specific player only
            await takaro.gameserver.gameServerControllerSendMessage(
                gameServerId,
                {
                    message,
                    opts: {
                        recipient: {
                            gameId: player.gameId
                        }
                    }
                }
            );

            // Update warning count with expiration time
            if (existingVariable.data.data.length) {
                await takaro.variable.variableControllerUpdate(existingVariable.data.data[0].id, {
                    value: currentWarnings.toString(),
                    expiresAt: expiresAt
                });
            } else {
                await takaro.variable.variableControllerCreate({
                    playerId: player.player.id,
                    gameServerId: gameServerId,
                    moduleId: mod.moduleId,
                    key: 'banned_items_warning',
                    value: currentWarnings.toString(),
                    expiresAt: expiresAt
                });
            }
        }
    }
}

await main();

