import { takaro, data, TakaroUserError, checkPermission } from '@takaro/helpers';

async function main() {
    const { player, gameServerId, module: mod } = data;

    // Check permission
    if (!checkPermission(data.pog, 'STOCK_MARKET_USE')) {
        throw new TakaroUserError("You don't have permission to use the stock market.");
    }

    // Get current stock data
    const marketDataVar = await takaro.variable.variableControllerSearch({
        filters: {
            key: ['stock_market_data'],
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId]
        }
    });

    if (marketDataVar.data.data.length === 0) {
        throw new TakaroUserError("The stock market isn't available right now. Please try again later.");
    }

    const stocks = JSON.parse(marketDataVar.data.data[0].value);

    // Get player's portfolio
    const portfolioVar = await takaro.variable.variableControllerSearch({
        filters: {
            key: ['stock_portfolio'],
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId],
            playerId: [player.id]
        }
    });

    let portfolio = {};
    if (portfolioVar.data.data.length > 0) {
        portfolio = JSON.parse(portfolioVar.data.data[0].value);
    }

    // Send header
    await player.pm("=== YOUR STOCK PORTFOLIO ===");

    let hasStocks = false;
    let totalValue = 0;
    let totalInvestment = 0;

    // If no stocks, send a simple message
    if (Object.keys(portfolio).length === 0) {
        await player.pm("You don't own any stocks yet.\n" +
            "Use /markets to see available stocks and prices.\n" +
            "Use /buy <stock> <amount> to purchase stocks.");
        return;
    }

    // Send each stock as a separate message to avoid length issues
    for (const [stockId, data] of Object.entries(portfolio)) {
        hasStocks = true;
        const stock = stocks.find(s => s.id === stockId);
        if (!stock) continue; // Stock might have been removed from config

        const currentValue = stock.price * data.shares;
        const investmentValue = data.averagePrice * data.shares;
        totalValue += currentValue;
        totalInvestment += investmentValue;
        const profit = currentValue - investmentValue;
        const profitPercent = ((profit / investmentValue) * 100).toFixed(1);

        let stockMessage = `--- ${stockId} (${stock.sector}) ---\n`;
        stockMessage += `Shares: ${data.shares}\n`;
        stockMessage += `Avg buy: $${Math.round(data.averagePrice)}\n`;
        stockMessage += `Current price: $${Math.round(stock.price)}\n`;
        stockMessage += `Total value: $${Math.round(currentValue)}\n`;

        if (profit >= 0) {
            stockMessage += `Profit: +$${Math.round(profit)} (+${profitPercent}%)\n`;
        } else {
            stockMessage += `Loss: -$${Math.abs(Math.round(profit))} (${profitPercent}%)\n`;
        }

        await player.pm(stockMessage);
    }

    if (hasStocks) {
        const totalProfit = totalValue - totalInvestment;
        const totalProfitPercent = ((totalProfit / totalInvestment) * 100).toFixed(1);

        let summaryMessage = "=== PORTFOLIO SUMMARY ===\n";
        summaryMessage += `Total investment: $${Math.round(totalInvestment)}\n`;
        summaryMessage += `Current value: $${Math.round(totalValue)}\n`;

        if (totalProfit >= 0) {
            summaryMessage += `Overall profit: +$${Math.round(totalProfit)} (+${totalProfitPercent}%)\n`;
        } else {
            summaryMessage += `Overall loss: -$${Math.abs(Math.round(totalProfit))} (${totalProfitPercent}%)\n`;
        }

        // Get transaction history count
        try {
            const historyVar = await takaro.variable.variableControllerSearch({
                filters: {
                    key: ['stock_transaction_history'],
                    gameServerId: [gameServerId],
                    moduleId: [mod.moduleId],
                    playerId: [player.id]
                }
            });

            if (historyVar.data.data.length > 0) {
                const history = JSON.parse(historyVar.data.data[0].value);
                const buyCount = history.filter(t => t.type === 'BUY').length;
                const sellCount = history.filter(t => t.type === 'SELL').length;

                summaryMessage += `\nTransactions: ${history.length} (${buyCount} buys, ${sellCount} sells)\n`;
            }
        } catch (error) {
            // Just ignore history errors
        }

        await player.pm(summaryMessage);

    }
}

