import { takaro, data, TakaroUserError, checkPermission } from '@takaro/helpers';

async function main() {
    const { player, gameServerId, module: mod, arguments: args } = data;

    // Check permission
    if (!checkPermission(data.pog, 'STOCK_MARKET_USE')) {
        throw new TakaroUserError("You don't have permission to use the stock market.");
    }

    // Handle the industry argument - use "ALL" as a special value to show all industries
    const industryFilter = args.Industry ? args.Industry.toUpperCase() : "ALL";

    // Get current stock data
    const marketDataVar = await takaro.variable.variableControllerSearch({
        filters: {
            key: ['stock_market_data'],
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId]
        }
    });

    // If market data doesn't exist, try to initialize it
    if (marketDataVar.data.data.length === 0) {
        const initialized = await initializeMarketIfNeeded(gameServerId, mod);
        if (!initialized) {
            throw new TakaroUserError("The stock market isn't available right now. Please try again later.");
        }

        // Get the freshly initialized market data
        const refreshedMarketData = await takaro.variable.variableControllerSearch({
            filters: {
                key: ['stock_market_data'],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId]
            }
        });

        if (refreshedMarketData.data.data.length === 0) {
            throw new TakaroUserError("There was an issue initializing the stock market. Please try again later.");
        }

        // Continue with the refreshed data
        await displayMarketSummary(player, gameServerId, mod, refreshedMarketData.data.data[0], industryFilter);
    } else {
        // Market data exists, display it
        await displayMarketSummary(player, gameServerId, mod, marketDataVar.data.data[0], industryFilter);
    }
}

// Display the market summary to the player
async function displayMarketSummary(player, gameServerId, mod, marketDataVariable, industryFilter) {
    // Get active market event
    const activeEventVar = await takaro.variable.variableControllerSearch({
        filters: {
            key: ['active_market_event'],
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId]
        }
    });

    let activeEvent = null;
    if (activeEventVar.data.data.length > 0 && activeEventVar.data.data[0].value) {
        try {
            activeEvent = JSON.parse(activeEventVar.data.data[0].value);
            // Handle empty string or empty object
            if (!activeEvent || Object.keys(activeEvent).length === 0) {
                activeEvent = null;
            }
        } catch (e) {
            // In case of parsing error
            activeEvent = null;
        }
    }

    const stocks = JSON.parse(marketDataVariable.value);

    // Get all available industries for reference
    const availableIndustries = [...new Set(stocks.map(stock => stock.sector))];

    // If industry filter is provided and not "ALL", check if it's valid
    if (industryFilter !== "ALL") {
        // Check if the industry exists
        const industryExists = availableIndustries.includes(industryFilter);

        if (!industryExists) {
            throw new TakaroUserError(`Industry "${industryFilter}" not found. Available industries: ${availableIndustries.join(', ')}\nUse "ALL" to view all industries.`);
        }
    }

    // Send header message
    let headerMessage = "=== STOCK MARKET SUMMARY ===\n";

    // If filtering by industry, mention it in the header
    if (industryFilter !== "ALL") {
        headerMessage = `=== ${industryFilter} INDUSTRY ===\n`;
    }

    // If there's an active event, include it in the header
    if (activeEvent) {
        headerMessage += `\nðŸŒ ACTIVE EVENT: ${activeEvent.name} ðŸŒ\n`;
        headerMessage += `${activeEvent.description}\n\n`;

        // If filtering by industry, only show relevant impacts
        if (industryFilter !== "ALL") {
            const relevantImpact = activeEvent.sectorImpacts.find(
                impact => impact.sectorId === industryFilter
            );

            if (relevantImpact) {
                const direction = relevantImpact.impact >= 0 ? "â†‘" : "â†“";
                headerMessage += `Industry Impact: ${direction} ${Math.abs(relevantImpact.impact)}%\n`;
            } else {
                headerMessage += "This industry is not directly affected by the current event.\n";
            }
        } else {
            // Show all industry impacts
            headerMessage += "Industry Impacts:\n";
            for (const impact of activeEvent.sectorImpacts) {
                const direction = impact.impact >= 0 ? "â†‘" : "â†“";
                headerMessage += `${impact.sectorId}: ${direction} ${Math.abs(impact.impact)}%\n`;
            }
        }
    }

    // Send header message first
    await player.pm(headerMessage);

    // Group stocks by industry
    const stocksByIndustry = {};
    stocks.forEach(stock => {
        if (!stocksByIndustry[stock.sector]) {
            stocksByIndustry[stock.sector] = [];
        }
        stocksByIndustry[stock.sector].push(stock);
    });

    // If industry filter is not "ALL", only show that industry
    if (industryFilter !== "ALL") {
        const filteredStocks = stocksByIndustry[industryFilter] || [];

        if (filteredStocks.length > 0) {
            let stockMessage = "";

            filteredStocks.forEach(stock => {
                let changeIcon = '';
                if (stock.lastPrice) {
                    const percentChange = ((stock.price - stock.lastPrice) / stock.lastPrice) * 100;
                    changeIcon = percentChange > 0 ? `â†‘ ${percentChange.toFixed(1)}%` :
                        percentChange < 0 ? `â†“ ${Math.abs(percentChange).toFixed(1)}%` : 'â†’';
                }

                stockMessage += `${stock.id}: $${Math.round(stock.price)} ${changeIcon}\n`;
            });

            await player.pm(stockMessage);
        } else {
            await player.pm(`No stocks found in the ${industryFilter} industry.`);
        }
    } else {
        // Send each industry as a separate message
        for (const industryId in stocksByIndustry) {
            let industryMessage = `=== ${industryId} INDUSTRY ===\n`;

            stocksByIndustry[industryId].forEach(stock => {
                let changeIcon = '';
                if (stock.lastPrice) {
                    const percentChange = ((stock.price - stock.lastPrice) / stock.lastPrice) * 100;
                    changeIcon = percentChange > 0 ? `â†‘ ${percentChange.toFixed(1)}%` :
                        percentChange < 0 ? `â†“ ${Math.abs(percentChange).toFixed(1)}%` : 'â†’';
                }

                industryMessage += `${stock.id}: $${Math.round(stock.price)} ${changeIcon}\n`;
            });

            await player.pm(industryMessage);
        }
    }

    // Add a help message for industry filtering if showing all industries
    if (industryFilter === "ALL") {
        const allIndustries = Object.keys(stocksByIndustry).join(', ');
        await player.pm(`\nTip: Use '/markets [industry]' to view only stocks in a specific industry. Available industries: ${allIndustries}`);
    }
}

// Initialize the market with stocks from the configuration
async function initializeMarketIfNeeded(gameServerId, mod) {
    try {
        // Get stocks from config
        const configStocks = (mod.userConfig && mod.userConfig.stocks) || [];

        if (!configStocks || configStocks.length === 0) {
            console.log("Error: No stocks defined in configuration");
            return false;
        }

        // Check for each required variable first
        const [marketDataVar, lastEventTimeVar, eventStartTimeVar, activeEventVar, marketInitVar] =
            await Promise.all([
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['stock_market_data'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['last_market_event_time'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['event_start_time'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['active_market_event'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['stock_market_initialized'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                })
            ]);

        // Map config stocks to our internal format
        const stocks = configStocks.map(stock => ({
            id: stock.id,
            name: stock.name,
            sector: stock.sector,
            price: stock.initialPrice,
            volatility: stock.volatility / 100, // Convert percentage to decimal
            lastPrice: stock.initialPrice
        }));

        // Create each variable only if it doesn't exist already
        const createPromises = [];

        // Store the stocks data if it doesn't exist
        if (marketDataVar.data.data.length === 0) {
            createPromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'stock_market_data',
                    value: JSON.stringify(stocks),
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Initialize last event time if it doesn't exist
        if (lastEventTimeVar.data.data.length === 0) {
            createPromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'last_market_event_time',
                    value: new Date().toISOString(),
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Initialize event start time variable if it doesn't exist
        if (eventStartTimeVar.data.data.length === 0) {
            createPromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'event_start_time',
                    value: new Date().toISOString(),
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Initialize active event if it doesn't exist
        if (activeEventVar.data.data.length === 0) {
            createPromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'active_market_event',
                    value: '',
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Mark market as initialized if not already marked
        if (marketInitVar.data.data.length === 0) {
            createPromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'stock_market_initialized',
                    value: 'true',
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Wait for all creation promises to complete
        if (createPromises.length > 0) {
            const results = await Promise.allSettled(createPromises);
            // Check if the critical stock_market_data was created successfully
            const stockDataPromise = results[0];
            if (stockDataPromise && stockDataPromise.status === 'rejected') {
                console.log(`Failed to create stock_market_data: ${stockDataPromise.reason}`);
                return false;
            }
        }

        return true;
    } catch (error) {
        console.log(`Error in initializeMarketIfNeeded: ${error.message}`);
        return false;
    }
}

await main();

import { takaro, data, TakaroUserError, checkPermission } from '@takaro/helpers';

async function main() {
    const { player, gameServerId, module: mod, arguments: args } = data;

    // Check permission
    if (!checkPermission(data.pog, 'STOCK_MARKET_USE')) {
        throw new TakaroUserError("You don't have permission to use the stock market.");
    }

    // Get current stock data
    const marketDataVar = await takaro.variable.variableControllerSearch({
        filters: {
            key: ['stock_market_data'],
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId]
        }
    });

    if (marketDataVar.data.data.length === 0) {
        throw new TakaroUserError("The stock market isn't available right now. Please try again later.");
    }

    const stocks = JSON.parse(marketDataVar.data.data[0].value);

    // If a specific stock ticker is provided, show detailed info for that stock
    const specificTicker = args.ticker ? args.ticker.toUpperCase() : null;

    if (specificTicker && specificTicker !== "ALL") {
        const stock = stocks.find(s => s.id.toUpperCase() === specificTicker);

        if (!stock) {
            throw new TakaroUserError(`Stock ${specificTicker} not found. Use /stockinfo without parameters to see all available stocks.`);
        }

        // Get active event to see if this stock's sector is affected
        const activeEventVar = await takaro.variable.variableControllerSearch({
            filters: {
                key: ['active_market_event'],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId]
            }
        });

        let activeEvent = null;
        let sectorImpact = null;

        if (activeEventVar.data.data.length > 0 && activeEventVar.data.data[0].value) {
            try {
                activeEvent = JSON.parse(activeEventVar.data.data[0].value);
                if (activeEvent && activeEvent.sectorImpacts) {
                    sectorImpact = activeEvent.sectorImpacts.find(impact =>
                        impact.sectorId === stock.sector
                    );
                }
            } catch (e) {
                // Ignore parsing errors
            }
        }

        // Calculate price change indicators
        let changeText = '';
        if (stock.lastPrice) {
            const percentChange = ((stock.price - stock.lastPrice) / stock.lastPrice) * 100;
            const changeIcon = percentChange > 0 ? 'â†‘' : percentChange < 0 ? 'â†“' : 'â†’';
            changeText = ` ${changeIcon} ${Math.abs(percentChange).toFixed(1)}%`;
        }

        // Add risk assessment based on volatility without showing the raw value
        let riskLevel;
        if (stock.volatility <= 0.05) riskLevel = "Very Low";
        else if (stock.volatility <= 0.10) riskLevel = "Low";
        else if (stock.volatility <= 0.15) riskLevel = "Moderate";
        else if (stock.volatility <= 0.20) riskLevel = "High";
        else riskLevel = "Very High";

        // Build detailed stock info message
        let message = `=== ${stock.id}: ${stock.name} ===\n\n`;
        message += `Sector: ${stock.sector}\n`;
        message += `Current Price: $${Math.round(stock.price)}${changeText}\n`;
        message += `Risk Level: ${riskLevel}\n`;

        // Add sector trend info if available from event
        if (activeEvent && sectorImpact) {
            const direction = sectorImpact.impact >= 0 ? "Positive" : "Negative";
            const strength = Math.abs(sectorImpact.impact);
            let trend;

            if (strength < 10) trend = "Slight";
            else if (strength < 25) trend = "Moderate";
            else trend = "Strong";

            message += `\nCurrent Trend: ${trend} ${direction} (${activeEvent.name})\n`;
            message += `Event: ${activeEvent.description}\n`;
        }

        // Add trading guidance based on sector and risk, without mentioning volatility
        message += `\nTrading Notes:\n`;

        if (activeEvent && sectorImpact) {
            if (sectorImpact.impact > 0) {
                message += `- Currently bullish due to the ${activeEvent.name} event\n`;
            } else {
                message += `- Currently bearish due to the ${activeEvent.name} event\n`;
            }
        }

        if (riskLevel === "High" || riskLevel === "Very High") {
            message += `- Expect significant price fluctuations with this stock\n`;
        } else if (riskLevel === "Low" || riskLevel === "Very Low") {
            message += `- Typically has stable price movement\n`;
        }

        message += `\nUse /buystock ${stock.id} [amount] to purchase shares`;

        await player.pm(message);

    } else {
        // No specific ticker provided or ALL specified, show summary of all stocks
        await player.pm("=== STOCK MARKET LISTINGS ===\n");

        // Group stocks by sector
        const stocksBySector = {};
        stocks.forEach(stock => {
            if (!stocksBySector[stock.sector]) {
                stocksBySector[stock.sector] = [];
            }
            stocksBySector[stock.sector].push(stock);
        });

        // Display stocks by sector
        for (const [sector, sectorStocks] of Object.entries(stocksBySector)) {
            let sectorMessage = `\n--- ${sector} SECTOR ---\n`;

            sectorStocks.forEach(stock => {
                // Add price change indicators
                let changeText = '';
                if (stock.lastPrice) {
                    const percentChange = ((stock.price - stock.lastPrice) / stock.lastPrice) * 100;
                    const changeIcon = percentChange > 0 ? 'â†‘' : percentChange < 0 ? 'â†“' : 'â†’';
                    changeText = ` ${changeIcon} ${Math.abs(percentChange).toFixed(1)}%`;
                }

                // Add risk level based on volatility
                let riskIndicator;
                if (stock.volatility <= 0.05) riskIndicator = "VL";
                else if (stock.volatility <= 0.10) riskIndicator = "L";
                else if (stock.volatility <= 0.15) riskIndicator = "M";
                else if (stock.volatility <= 0.20) riskIndicator = "H";
                else riskIndicator = "VH";

                sectorMessage += `${stock.id} (${riskIndicator}): ${stock.name} - $${Math.round(stock.price)}${changeText}\n`;
            });

            await player.pm(sectorMessage);
        }

        // Add legend for risk indicators
        const legend = "\n=== LEGEND ===\n" +
            "Risk Levels: VL=Very Low, L=Low, M=Moderate, H=High, VH=Very High\n" +
            "Use /stockinfo [ticker] for detailed information about a specific stock";

        await player.pm(legend);
    }
}

