import { takaro, data, TakaroUserError, checkPermission } from '@takaro/helpers';

async function main() {
    const { player, gameServerId, module: mod, arguments: args } = data;

    // Check permission
    if (!checkPermission(data.pog, 'STOCK_MARKET_TRADE')) {
        throw new TakaroUserError("You don't have permission to trade stocks.");
    }

    // Validate input parameters
    if (!args.stock) {
        throw new TakaroUserError("Please specify a stock ticker. Usage: /buystock STOCK AMOUNT");
    }

    // Convert amount to integer and validate
    const amount = parseInt(args.amount);
    if (isNaN(amount) || amount <= 0) {
        throw new TakaroUserError("Amount must be a positive whole number. Usage: /buystock STOCK AMOUNT");
    }

    try {
        // Get current stock data
        const marketDataVar = await takaro.variable.variableControllerSearch({
            filters: {
                key: ['stock_market_data'],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId]
            }
        });

        if (marketDataVar.data.data.length === 0) {
            throw new TakaroUserError("The stock market isn't available right now. Please try again later.");
        }

        const stocks = JSON.parse(marketDataVar.data.data[0].value);
        const stock = stocks.find(s => s.id.toUpperCase() === args.stock.toUpperCase());

        if (!stock) {
            throw new TakaroUserError(`Stock ${args.stock} not found. Use /market to see available stocks.`);
        }

        // Calculate transaction fee - using integers to avoid floating point issues
        let feePercentage = mod.userConfig.transactionFee || 5;

        // Check for VIP discount
        if (checkPermission(data.pog, 'STOCK_MARKET_BROKER')) {
            const discount = (mod.userConfig.vipDiscount || 50) / 100; // Convert to decimal
            feePercentage = feePercentage * (1 - discount);
        }

        // Calculate costs using Math.round to ensure we work with integers
        const subtotal = Math.round(stock.price) * amount;
        const fee = Math.round((subtotal * feePercentage) / 100);
        const totalCost = subtotal + fee;

        // Check if player has enough currency
        const playerData = await takaro.playerOnGameserver.playerOnGameServerControllerGetOne(gameServerId, player.id);
        const currentBalance = playerData.data.data.currency;

        if (currentBalance < totalCost) {
            throw new TakaroUserError(`You don't have enough currency. Cost: $${subtotal} + $${fee} fee = $${totalCost}. Your balance: $${currentBalance}`);
        }

        // Deduct the currency - using integer value to avoid precision errors
        await takaro.playerOnGameserver.playerOnGameServerControllerDeductCurrency(
            gameServerId,
            player.id,
            {
                currency: totalCost
            }
        );

        // Get player's portfolio or create new one
        const portfolioVar = await takaro.variable.variableControllerSearch({
            filters: {
                key: ['stock_portfolio'],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId],
                playerId: [player.id]
            }
        });

        let portfolio;
        if (portfolioVar.data.data.length === 0) {
            portfolio = {};
        } else {
            portfolio = JSON.parse(portfolioVar.data.data[0].value);
        }

        // Update portfolio using rounded values for consistency
        const stockPrice = Math.round(stock.price);
        if (!portfolio[stock.id]) {
            portfolio[stock.id] = {
                shares: amount,
                averagePrice: stockPrice
            };
        } else {
            const totalShares = portfolio[stock.id].shares + amount;
            const totalValue = (portfolio[stock.id].shares * portfolio[stock.id].averagePrice) + (amount * stockPrice);
            portfolio[stock.id].shares = totalShares;
            portfolio[stock.id].averagePrice = Math.round(totalValue / totalShares);
        }

        // Save updated portfolio
        if (portfolioVar.data.data.length === 0) {
            await takaro.variable.variableControllerCreate({
                key: 'stock_portfolio',
                value: JSON.stringify(portfolio),
                gameServerId,
                moduleId: mod.moduleId,
                playerId: player.id
            });
        } else {
            await takaro.variable.variableControllerUpdate(portfolioVar.data.data[0].id, {
                value: JSON.stringify(portfolio)
            });
        }

        // Track this transaction in transaction history
        await recordTransaction(gameServerId, mod.moduleId, player.id, {
            type: 'BUY',
            stockId: stock.id,
            shares: amount,
            pricePerShare: stockPrice,
            subtotal: subtotal,
            fee: fee,
            total: totalCost,
            timestamp: new Date().toISOString()
        });

        let message = `Successfully bought ${amount} shares of ${stock.id} at $${stockPrice} each.\n`;
        message += `Subtotal: $${subtotal}\n`;
        message += `Transaction fee: $${fee}\n`;
        message += `Total cost: $${totalCost}\n`;
        message += `Current portfolio: ${portfolio[stock.id].shares} shares of ${stock.id}`;

