import { takaro, data, TakaroUserError, checkPermission } from '@takaro/helpers';

async function main() {
    const { player, gameServerId, module: mod, arguments: args } = data;

    // Check permission
    if (!checkPermission(data.pog, 'STOCK_MARKET_TRADE')) {
        throw new TakaroUserError("You don't have permission to trade stocks.");
    }

    // Validate input parameters
    if (!args.stock) {
        throw new TakaroUserError("Please specify a stock ticker. Usage: /sellstock STOCK AMOUNT");
    }

    // Convert amount to integer and validate
    const amount = parseInt(args.amount);
    if (isNaN(amount) || amount <= 0) {
        throw new TakaroUserError("Amount must be a positive whole number. Usage: /sellstock STOCK AMOUNT");
    }

    try {
        // Get player's portfolio
        const portfolioVar = await takaro.variable.variableControllerSearch({
            filters: {
                key: ['stock_portfolio'],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId],
                playerId: [player.id]
            }
        });

        if (portfolioVar.data.data.length === 0) {
            throw new TakaroUserError("You don't own any stocks to sell.");
        }

        const portfolio = JSON.parse(portfolioVar.data.data[0].value);
        const stockId = args.stock.toUpperCase();

        if (!portfolio[stockId] || portfolio[stockId].shares < amount) {
            throw new TakaroUserError(`You don't own ${amount} shares of ${stockId}.`);
        }

        // Get current stock data
        const marketDataVar = await takaro.variable.variableControllerSearch({
            filters: {
                key: ['stock_market_data'],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId]
            }
        });

        if (marketDataVar.data.data.length === 0) {
            throw new TakaroUserError("The stock market isn't available right now. Please try again later.");
        }

        const stocks = JSON.parse(marketDataVar.data.data[0].value);
        const stock = stocks.find(s => s.id.toUpperCase() === stockId);

        if (!stock) {
            throw new TakaroUserError(`Stock ${stockId} not found in current market data. Please contact an admin.`);
        }

        // Calculate transaction fee - using integers to avoid floating point issues
        let feePercentage = mod.userConfig.transactionFee || 5;

        // Check for VIP discount
        if (checkPermission(data.pog, 'STOCK_MARKET_BROKER')) {
            const discount = (mod.userConfig.vipDiscount || 50) / 100; // Convert to decimal
            feePercentage = feePercentage * (1 - discount);
        }

        // Calculate sale proceeds using Math.round to ensure we work with integers
        const stockPrice = Math.round(stock.price);
        const subtotal = stockPrice * amount;
        const fee = Math.round((subtotal * feePercentage) / 100);
        const netProceeds = subtotal - fee;

        // Add money to player - using integer value to avoid precision errors
        await takaro.playerOnGameserver.playerOnGameServerControllerAddCurrency(
            gameServerId,
            player.id,
            {
                currency: netProceeds
            }
        );

        // Calculate profit/loss
        const profitPerShare = stockPrice - portfolio[stockId].averagePrice;
        const totalProfit = Math.round(profitPerShare * amount);
        const profitText = totalProfit >= 0
            ? `profit of $${totalProfit}`
            : `loss of $${Math.abs(totalProfit)}`;

        // Update portfolio
        portfolio[stockId].shares -= amount;
        if (portfolio[stockId].shares === 0) {
            delete portfolio[stockId];
        }

        // Save updated portfolio
        await takaro.variable.variableControllerUpdate(portfolioVar.data.data[0].id, {
            value: JSON.stringify(portfolio)
        });

        // Track this transaction in transaction history
        await recordTransaction(gameServerId, mod.moduleId, player.id, {
            type: 'SELL',
            stockId: stock.id,
            shares: amount,
            pricePerShare: stockPrice,
            subtotal: subtotal,
            fee: fee,
            total: netProceeds,
            profit: totalProfit,
            timestamp: new Date().toISOString()
        });

        let message = `Successfully sold ${amount} shares of ${stockId} at $${stockPrice} each.\n`;
        message += `Subtotal: $${subtotal}\n`;
        message += `Transaction fee: $${fee}\n`;
        message += `Net proceeds: $${netProceeds} (${profitText})`;

        // Add remaining shares info if player still has some
        if (portfolio[stockId]) {
            message += `\nRemaining shares: ${portfolio[stockId].shares}`;
        } else {
            message += `\nYou've sold all your ${stockId} shares.`;
        }

        await player.pm(message);

    } catch (error) {
        // If it's our custom error, just pass it through
        if (error instanceof TakaroUserError) {
            throw error;
        }

        // For unexpected errors, log them and provide a friendlier message
        console.error("Error in sellstock command:", error);
        throw new TakaroUserError("An error occurred while processing your sale. Please try again later.");
    }
}

