import { takaro, data, TakaroUserError, checkPermission } from '@takaro/helpers';

async function main() {
    const { player, gameServerId, module: mod, arguments: args } = data;

    // Check permission
    if (!checkPermission(data.pog, 'STOCK_MARKET_TRIGGER_EVENT')) {
        throw new TakaroUserError("You don't have permission to trigger market events.");
    }

    try {
        // Get current stock data to check market initialization
        const marketDataVar = await takaro.variable.variableControllerSearch({
            filters: {
                key: ['stock_market_data'],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId]
            }
        });

        // Initialize market if needed
        if (marketDataVar.data.data.length === 0) {
            await initializeMarketIfNeeded(gameServerId, mod);
            await player.pm("Market was not initialized. Initializing now...");
            return;
        }

        // Get events from module config
        const marketEvents = mod.userConfig.marketEvents || [];

        if (marketEvents.length === 0) {
            throw new TakaroUserError("No market events configured in this module.");
        }

        // Handle case when no event is specified or "ALL" is provided
        if (!args.EventName || args.EventName.toUpperCase() === "ALL") {
            await player.pm("=== AVAILABLE MARKET EVENTS ===\n");

            // Group events by category or type if possible
            const eventCategories = {};

            // Create a simple categorization based on positive/negative impact
            marketEvents.forEach(event => {
                let category = "Mixed";
                // Calculate net impact across all sectors
                const netImpact = event.sectorImpacts.reduce((sum, impact) => sum + impact.impact, 0);

                if (netImpact > 0) category = "Positive";
                else if (netImpact < 0) category = "Negative";

                if (!eventCategories[category]) {
                    eventCategories[category] = [];
                }
                eventCategories[category].push(event);
            });

            // Display events by category
            for (const [category, events] of Object.entries(eventCategories)) {
                if (events.length > 0) {
                    await player.pm(`\n--- ${category.toUpperCase()} EVENTS ---`);

                    let message = "";
                    events.forEach(event => {
                        // Format primary sectors affected
                        const primarySectors = event.sectorImpacts
                            .filter(impact => Math.abs(impact.impact) >= 15)
                            .map(impact => {
                                const direction = impact.impact >= 0 ? "â†‘" : "â†“";
                                return `${impact.sectorId} ${direction}${Math.abs(impact.impact)}%`;
                            })
                            .join(", ");

                        message += `${event.id}: ${event.name}\n`;
                        message += `   ${event.description}\n`;
                        if (primarySectors) {
                            message += `   Major impacts: ${primarySectors}\n`;
                        }
                        message += "\n";
                    });

                    await player.pm(message);
                }
            }

            await player.pm("=== HOW TO USE ===\nUse `/triggerevent <EventName>` to trigger a specific event (e.g., `/triggerevent TECH_BOOM`)");
            return;
        }

        // Find the requested event
        const eventId = args.EventName.toUpperCase();
        const event = marketEvents.find(e => e.id === eventId);

        if (!event) {
            throw new TakaroUserError(`Event "${eventId}" not found. Use /triggerevent without parameters to see all available events.`);
        }

        // Get necessary variables for managing the event
        const [activeEventVar, eventStartTimeVar, eventCounterVar, cooldownCounterVar, eventDurationVar] =
            await Promise.all([
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['active_market_event'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['event_start_time'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['event_execution_counter'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['event_cooldown_counter'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['current_event_duration'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                })
            ]);

        // Check if there's an active event we need to cancel
        let activeEventObj = null;
        if (activeEventVar.data.data.length > 0 && activeEventVar.data.data[0].value) {
            try {
                activeEventObj = JSON.parse(activeEventVar.data.data[0].value);
                if (activeEventObj && Object.keys(activeEventObj).length > 0) {
                    await player.pm(`Cancelling active event "${activeEventObj.name}" to trigger new event.`);
                }
            } catch (e) {
                // Invalid event data, will be overwritten
            }
        }

        // Generate a random duration for the event
        const maxDuration = (mod.userConfig && mod.userConfig.defaultEventDuration) || 5;
        const randomDuration = Math.floor(Math.random() * maxDuration) + 1; // 1 to maxDuration

        // Update or create all event-related variables
        const updatePromises = [];

        // Save or update the random duration
        if (eventDurationVar.data.data.length > 0) {
            updatePromises.push(
                takaro.variable.variableControllerUpdate(eventDurationVar.data.data[0].id, {
                    value: randomDuration.toString()
                })
            );
        } else {
            updatePromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'current_event_duration',
                    value: randomDuration.toString(),
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Reset the event counter to 0
        if (eventCounterVar.data.data.length > 0) {
            updatePromises.push(
                takaro.variable.variableControllerUpdate(eventCounterVar.data.data[0].id, {
                    value: '0'
                })
            );
        } else {
            updatePromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'event_execution_counter',
                    value: '0',
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Reset the cooldown counter
        if (cooldownCounterVar.data.data.length > 0) {
            updatePromises.push(
                takaro.variable.variableControllerUpdate(cooldownCounterVar.data.data[0].id, {
                    value: '0'
                })
            );
        } else {
            updatePromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'event_cooldown_counter',
                    value: '0',
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Update active event
        if (activeEventVar.data.data.length > 0) {
            updatePromises.push(
                takaro.variable.variableControllerUpdate(activeEventVar.data.data[0].id, {
                    value: JSON.stringify(event)
                })
            );
        } else {
            updatePromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'active_market_event',
                    value: JSON.stringify(event),
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Update event start time
        if (eventStartTimeVar.data.data.length > 0) {
            updatePromises.push(
                takaro.variable.variableControllerUpdate(eventStartTimeVar.data.data[0].id, {
                    value: new Date().toISOString()
                })
            );
        } else {
            updatePromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'event_start_time',
                    value: new Date().toISOString(),
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Update last event time
        const lastEventTimeVar = await takaro.variable.variableControllerSearch({
            filters: {
                key: ['last_market_event_time'],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId]
            }
        });

        if (lastEventTimeVar.data.data.length > 0) {
            updatePromises.push(
                takaro.variable.variableControllerUpdate(lastEventTimeVar.data.data[0].id, {
                    value: new Date().toISOString()
                })
            );
        } else {
            updatePromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'last_market_event_time',
                    value: new Date().toISOString(),
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Wait for all updates to complete
        await Promise.all(updatePromises);

        // Check if there are online players to announce the event
        const onlinePlayers = await takaro.playerOnGameserver.playerOnGameServerControllerSearch({
            filters: {
                gameServerId: [gameServerId],
                online: [true]
            }
        });

        if (onlinePlayers.data.meta.total > 0) {
            // Format sector impacts for announcement
            let impactText = "";
            for (const impact of event.sectorImpacts) {
                const direction = impact.impact >= 0 ? "â†‘" : "â†“";
                impactText += `\n${impact.sectorId}: ${direction} ${Math.abs(impact.impact)}%`;
            }

            const message = `ðŸŒ BREAKING MARKET NEWS ðŸŒ\n\n${event.name}\n${event.description}\n\nSector Impacts:${impactText}\n\nThis event will influence stock prices! Use /markets to see opportunities!`;

            // Split the message if it's too long
            const maxLength = 400; // Safe limit for most games
            for (let i = 0; i < message.length; i += maxLength) {
                await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                    message: message.substring(i, i + maxLength)
                });
            }
        }

        await player.pm(`Successfully triggered the "${event.name}" market event for ${randomDuration} cycles!`);
    } catch (error) {
        // If something goes wrong, log it and let the player know
        console.log(`Error in triggerEvent: ${error.message}`);
        throw new TakaroUserError(`Error triggering event: ${error.message}`);
    }
}

// Initialize the market with stocks from the configuration
async function initializeMarketIfNeeded(gameServerId, mod) {
    try {
        // Get stocks from config
        const configStocks = (mod.userConfig && mod.userConfig.stocks) || [];

        if (!configStocks || configStocks.length === 0) {
            console.log("Error: No stocks defined in configuration");
            return;
        }

        // Map config stocks to our internal format
        const stocks = configStocks.map(stock => ({
            id: stock.id,
            name: stock.name,
            sector: stock.sector,
            price: stock.initialPrice,
            volatility: stock.volatility / 100, // Convert percentage to decimal
            lastPrice: stock.initialPrice
        }));

        // Store the stocks data
        await takaro.variable.variableControllerCreate({
            key: 'stock_market_data',
            value: JSON.stringify(stocks),
            gameServerId,
            moduleId: mod.moduleId
        });

        // Initialize last event time to now
        await takaro.variable.variableControllerCreate({
            key: 'last_market_event_time',
            value: new Date().toISOString(),
            gameServerId,
            moduleId: mod.moduleId
        });

        // Initialize event start time variable
        await takaro.variable.variableControllerCreate({
            key: 'event_start_time',
            value: new Date().toISOString(),
            gameServerId,
            moduleId: mod.moduleId
        });

        // Initialize active event (empty string means no active event)
        await takaro.variable.variableControllerCreate({
            key: 'active_market_event',
            value: '',
            gameServerId,
            moduleId: mod.moduleId
        });

        // Mark market as initialized
        await takaro.variable.variableControllerCreate({
            key: 'stock_market_initialized',
            value: 'true',
            gameServerId,
            moduleId: mod.moduleId
        });

        // Announce market initialization to online players
        const onlinePlayers = await takaro.playerOnGameserver.playerOnGameServerControllerSearch({
            filters: {
                gameServerId: [gameServerId],
                online: [true]
            }
        });

        if (onlinePlayers.data.meta.total > 0) {
            const message = "ðŸ“ˆ STOCK MARKET INITIALIZED ðŸ“ˆ\n\nThe stock market is now open for trading! Use /markets to see available stocks and /buystock to start investing.";

            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                message
            });
        }
    } catch (error) {
        console.log(`Error in initializeMarketIfNeeded: ${error.message}`);
    }
}

