        let eventDuration = (mod.userConfig && mod.userConfig.defaultEventDuration) || 5;
        if (eventDurationVar.data.data.length > 0) {
            eventDuration = parseInt(eventDurationVar.data.data[0].value, 10);
        }

        let activeEvent = null;
        if (activeEventVar.data.data.length > 0 && activeEventVar.data.data[0].value) {
            try {
                activeEvent = JSON.parse(activeEventVar.data.data[0].value);
                // If it's an empty string, treat as no active event
                if (!activeEvent || Object.keys(activeEvent).length === 0) {
                    activeEvent = null;
                }
            } catch (e) {
                // In case of parsing error, consider no active event
                activeEvent = null;
            }
        }

        // Check if the current event should end and increment counter
        if (activeEvent) {
            // Increment the event execution counter
            eventCounter++;

            // Update or create the counter
            if (eventCounterVar.data.data.length > 0) {
                await takaro.variable.variableControllerUpdate(eventCounterVar.data.data[0].id, {
                    value: eventCounter.toString()
                });
            } else {
                await takaro.variable.variableControllerCreate({
                    key: 'event_execution_counter',
                    value: eventCounter.toString(),
                    gameServerId,
                    moduleId: mod.moduleId
                });
            }

            // End the event if counter reached duration
            if (eventCounter >= eventDuration) {
                // Clear the active event
                if (activeEventVar.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(activeEventVar.data.data[0].id, {
                        value: ''
                    });
                }

                // Reset counter to 0
                if (eventCounterVar.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(eventCounterVar.data.data[0].id, {
                        value: '0'
                    });
                }

                // Reset cooldown counter to 0 to begin cooldown period
                if (cooldownCounterVar.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(cooldownCounterVar.data.data[0].id, {
                        value: '0'
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: 'event_cooldown_counter',
                        value: '0',
                        gameServerId,
                        moduleId: mod.moduleId
                    });
                }

                // Announce the end of the event to all online players
                const onlinePlayers = await takaro.playerOnGameserver.playerOnGameServerControllerSearch({
                    filters: {
                        gameServerId: [gameServerId],
                        online: [true]
                    }
                });

                if (onlinePlayers.data.meta.total > 0) {
                    const message = `ðŸ“ˆ MARKET UPDATE ðŸ“‰\n\nThe "${activeEvent.name}" event has ended. Markets are returning to normal conditions.`;

                    await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                        message: message
                    });
                }

                // Reset active event
                activeEvent = null;
            }
        } else {
            // No active event, increment cooldown counter
            cooldownCounter++;

            // Update or create the cooldown counter
            if (cooldownCounterVar.data.data.length > 0) {
                await takaro.variable.variableControllerUpdate(cooldownCounterVar.data.data[0].id, {
                    value: cooldownCounter.toString()
                });
            } else {
                await takaro.variable.variableControllerCreate({
                    key: 'event_cooldown_counter',
                    value: cooldownCounter.toString(),
                    gameServerId,
                    moduleId: mod.moduleId
                });
            }
        }

        // Get last event time for event frequency calculation
        const lastEventTimeVar = await takaro.variable.variableControllerSearch({
            filters: {
                key: ['last_market_event_time'],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId]
            }
        });

        const eventFrequency = (mod.userConfig && mod.userConfig.eventFrequency) || 10;
        let shouldTriggerEvent = false;

        // Only trigger a new event if:
        // 1. There's no active event 
        // 2. We have passed the cooldown period (cooldownCounter >= eventFrequency)
        if (!activeEvent && cooldownCounter >= eventFrequency) {
            // Base chance to trigger an event
            shouldTriggerEvent = Math.random() < 0.5; // 50% chance

            // Force an event if we're well past the cooldown period (2x frequency)
            if (cooldownCounter >= eventFrequency * 2) {
                shouldTriggerEvent = true;
            }
        }

        // If we should trigger a new event, select a random one
        if (shouldTriggerEvent) {
            const events = (mod.userConfig && mod.userConfig.marketEvents) || [];
            if (events.length > 0) {
                const randomEvent = events[Math.floor(Math.random() * events.length)];
                activeEvent = randomEvent;

                // Generate a random duration between 1 and defaultEventDuration
                const maxDuration = (mod.userConfig && mod.userConfig.defaultEventDuration) || 5;
                const randomDuration = Math.floor(Math.random() * maxDuration) + 1; // 1 to maxDuration

                // Save the random duration
                if (eventDurationVar.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(eventDurationVar.data.data[0].id, {
                        value: randomDuration.toString()
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: 'current_event_duration',
                        value: randomDuration.toString(),
                        gameServerId,
                        moduleId: mod.moduleId
                    });
                }

                // Reset the event counter to 0
                if (eventCounterVar.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(eventCounterVar.data.data[0].id, {
                        value: '0'
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: 'event_execution_counter',
                        value: '0',
                        gameServerId,
                        moduleId: mod.moduleId
                    });
                }

                // Reset the cooldown counter
                if (cooldownCounterVar.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(cooldownCounterVar.data.data[0].id, {
                        value: '0'
                    });
                }

                // Update active event
                if (activeEventVar.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(activeEventVar.data.data[0].id, {
                        value: JSON.stringify(activeEvent)
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: 'active_market_event',
                        value: JSON.stringify(activeEvent),
                        gameServerId,
                        moduleId: mod.moduleId
                    });
                }

                // Update event start time
                if (eventStartTimeVar.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(eventStartTimeVar.data.data[0].id, {
                        value: new Date().toISOString()
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: 'event_start_time',
                        value: new Date().toISOString(),
                        gameServerId,
                        moduleId: mod.moduleId
                    });
                }

                // Update last event time
                if (lastEventTimeVar.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(lastEventTimeVar.data.data[0].id, {
                        value: new Date().toISOString()
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: 'last_market_event_time',
                        value: new Date().toISOString(),
                        gameServerId,
                        moduleId: mod.moduleId
                    });
                }

                // Announce the event to all online players
                const onlinePlayers = await takaro.playerOnGameserver.playerOnGameServerControllerSearch({
                    filters: {
                        gameServerId: [gameServerId],
                        online: [true]
                    }
                });

                if (onlinePlayers.data.meta.total > 0) {
                    // Format sector impacts for announcement
                    let impactText = "";
                    for (const impact of activeEvent.sectorImpacts) {
                        const direction = impact.impact >= 0 ? "â†‘" : "â†“";
                        impactText += `\n${impact.sectorId}: ${direction} ${Math.abs(impact.impact)}%`;
                    }

                    const message = `ðŸŒ BREAKING MARKET NEWS ðŸŒ\n\n${activeEvent.name}\n${activeEvent.description}\n\nSector Impacts:${impactText}\n\nThis event will influence stock prices! Use /market to see opportunities!`;

                    // Split the message if it's too long
                    const maxLength = 400; // Safe limit for most games
                    for (let i = 0; i < message.length; i += maxLength) {
                        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                            message: message.substring(i, i + maxLength)
                        });
                    }
                }
            }
        }

        // Get stocks
        if (!marketDataVar.data.data[0] || !marketDataVar.data.data[0].value) {
            // Re-initialize market if value is missing
            await initializeMarketIfNeeded(gameServerId, mod);
            return;
        }

        let stocks;
        try {
            stocks = JSON.parse(marketDataVar.data.data[0].value);
            if (!Array.isArray(stocks)) {
                throw new Error("Parsed stocks data is not an array");
            }
        } catch (e) {
            // Re-initialize market if data is corrupt
            await initializeMarketIfNeeded(gameServerId, mod);
            return;
        }

        const significantChanges = [];

        // Update each stock price
        stocks.forEach(stock => {
            // Save the current price as lastPrice for change tracking
            if (!stock.lastPrice) {
                stock.lastPrice = stock.price;
            } else {
                stock.lastPrice = stock.price;
            }

            // Calculate base price change based on volatility
            // Using a more normalized random approach
            // Math.random() * 2 - 1 gives a value between -1 and 1
            const randomFactor = Math.random() * 2 - 1;
            const baseChangePercent = randomFactor * stock.volatility;
            let totalChangePercent = baseChangePercent;

            // Apply active event effects if any
            if (activeEvent) {
                const sectorImpact = activeEvent.sectorImpacts.find(impact => impact.sectorId === stock.sector);
                if (sectorImpact) {
                    // Convert impact percentage to decimal and apply a random factor
                    // to create varied effects within each sector
                    const eventImpactPercentage = sectorImpact.impact; // This is already a percentage
                    const eventImpactDecimal = eventImpactPercentage / 100; // Convert to decimal
                    const randomImpactFactor = 0.5 + Math.random(); // Between 0.5 and 1.5
                    const eventImpact = eventImpactDecimal * randomImpactFactor;

                    // Add the event impact to the total change percent
                    totalChangePercent += eventImpact;
                }
            }

            // Apply the price change
            const oldPrice = stock.price;
            const priceChange = stock.price * totalChangePercent;
            stock.price = Math.max(1, stock.price + priceChange);

            // Check if this is a significant change
            const changeThreshold = ((mod.userConfig && mod.userConfig.priceAlertThreshold) || 10) / 100;
            const percentChange = (stock.price - stock.lastPrice) / stock.lastPrice;

            if (Math.abs(percentChange) > changeThreshold) {
                significantChanges.push({
                    ...stock,
                    changePercent: percentChange * 100
                });
            }
        });

        // Save updated prices
        await takaro.variable.variableControllerUpdate(marketDataVar.data.data[0].id, {
            value: JSON.stringify(stocks)
        });

        // Broadcast major changes to all players
        if (significantChanges.length > 0) {
            const onlinePlayers = await takaro.playerOnGameserver.playerOnGameServerControllerSearch({
                filters: {
                    gameServerId: [gameServerId],
                    online: [true]
                }
            });

            if (onlinePlayers.data.meta.total > 0) {
                // Sort by absolute change percentage
                significantChanges.sort((a, b) => Math.abs(b.changePercent) - Math.abs(a.changePercent));

                // Take top 3 most significant changes
                const topChanges = significantChanges.slice(0, Math.min(3, significantChanges.length));

                // Formulate message about big market changes
                let message = "ðŸ“Š STOCK MARKET ALERT ðŸ“Š\n";
                topChanges.forEach(stock => {
                    const changeDir = stock.changePercent > 0 ? "up" : "down";
                    const changePercent = Math.abs(Math.round(stock.changePercent));

                    message += `${stock.id} (${stock.sector}): ${changeDir} ${changePercent}% to $${Math.round(stock.price)}!\n`;
                });

                if (activeEvent) {
                    message += `\nCurrent market event: ${activeEvent.name}`;
                }

                // Send the message
                await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                    message
                });
            }
        }
    } catch (error) {
        // If something goes wrong, log it
        console.log(`Error in updateStockPrices: ${error.message}`);
    }
}

// Initialize the market with stocks from the configuration
async function initializeMarketIfNeeded(gameServerId, mod) {
    try {
        // Get stocks from config
        const configStocks = (mod.userConfig && mod.userConfig.stocks) || [];

        if (!configStocks || configStocks.length === 0) {
            console.log("Error: No stocks defined in configuration");
            return;
        }

        // Check for each required variable first
        const [marketDataVar, lastEventTimeVar, eventStartTimeVar, activeEventVar, marketInitVar] =
            await Promise.all([
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['stock_market_data'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['last_market_event_time'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['event_start_time'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['active_market_event'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                }),
                takaro.variable.variableControllerSearch({
                    filters: {
                        key: ['stock_market_initialized'],
                        gameServerId: [gameServerId],
                        moduleId: [mod.moduleId]
                    }
                })
            ]);

        // Map config stocks to our internal format
        const stocks = configStocks.map(stock => ({
            id: stock.id,
            name: stock.name,
            sector: stock.sector,
            price: stock.initialPrice,
            volatility: stock.volatility / 100, // Convert percentage to decimal
            lastPrice: stock.initialPrice
        }));

        // Create each variable only if it doesn't exist already
        const createPromises = [];

        // Store the stocks data if it doesn't exist
        if (marketDataVar.data.data.length === 0) {
            createPromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'stock_market_data',
                    value: JSON.stringify(stocks),
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Initialize last event time if it doesn't exist
        if (lastEventTimeVar.data.data.length === 0) {
            createPromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'last_market_event_time',
                    value: new Date().toISOString(),
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Initialize event start time variable if it doesn't exist
        if (eventStartTimeVar.data.data.length === 0) {
            createPromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'event_start_time',
                    value: new Date().toISOString(),
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Initialize active event if it doesn't exist
        if (activeEventVar.data.data.length === 0) {
            createPromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'active_market_event',
                    value: '',
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Mark market as initialized if not already marked
        if (marketInitVar.data.data.length === 0) {
            createPromises.push(
                takaro.variable.variableControllerCreate({
                    key: 'stock_market_initialized',
                    value: 'true',
                    gameServerId,
                    moduleId: mod.moduleId
                })
            );
        }

        // Wait for all creation promises to complete
        if (createPromises.length > 0) {
            await Promise.allSettled(createPromises);
        }

        // Only announce if we had to create at least the stock data (indicating a new market)
        if (marketDataVar.data.data.length === 0) {
            // Announce market initialization to online players
            const onlinePlayers = await takaro.playerOnGameserver.playerOnGameServerControllerSearch({
                filters: {
                    gameServerId: [gameServerId],
                    online: [true]
                }
            });

            if (onlinePlayers.data.meta.total > 0) {
                const message = "ðŸ“ˆ STOCK MARKET INITIALIZED ðŸ“ˆ\n\nThe stock market is now open for trading! Use /market to see available stocks and /buy to start investing.";

                await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                    message
                });
            }
        }
    } catch (error) {
        // Log the error but don't throw, so the cronjob can continue
        console.log(`Error in initializeMarketIfNeeded: ${error.message}`);
    }
}

await main();
