import { takaro, data, checkPermission, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { player, module: mod, gameServerId, arguments: args } = data;

    // Check permissions
    if (!checkPermission(data.pog, 'SERVER_WIPE')) {
        throw new TakaroUserError('You do not have permission to wipe server data.');
    }

    // Check if confirmation is provided
    if (!args.confirm) {
        throw new TakaroUserError(`
This will wipe server data! To confirm, use one of:
/wipe all true - Wipe everything (respects config settings)
/wipe currency true - Wipe only currency
/wipe teleports true - Wipe all teleports (including waypoints)
/wipe starterkits true - Wipe only starter kit claims`);
    }

    // Start the wipe process
    await player.pm('Server wipe initiated. This might take a moment...');

    const wipeAll = args.target === 'all';

    // 1. Wipe currency if specified and enabled in config
    if ((wipeAll && mod.userConfig.wipeCurrency) || args.target === 'currency') {
        try {
            await player.pm("Resetting all player currency...");

            // First get all PlayerOnGameServer records
            const pogsResponse = await takaro.playerOnGameserver.playerOnGameServerControllerSearch({
                filters: {
                    gameServerId: [gameServerId]
                },
                limit: 100
            });

            const pogs = pogsResponse.data.data;

            if (!pogs || pogs.length === 0) {
                await player.pm('No players found on this server.');
                return;
            }

            // Process players in batches of 10 using Promise.all
            const batchSize = 10;
            let resetCount = 0;
            let errorCount = 0;

            for (let i = 0; i < pogs.length; i += batchSize) {
                const batch = pogs.slice(i, i + batchSize);

                const results = await Promise.all(
                    batch.map(pog => {
                        return takaro.playerOnGameserver.playerOnGameServerControllerSetCurrency(
                            gameServerId,
                            pog.playerId,
                            { currency: 0 }
                        )
                            .then(() => ({ success: true }))
                            .catch(err => ({
                                success: false,
                                error: err,
                                playerId: pog.playerId
                            }));
                    })
                );

                // Count successes and failures
                results.forEach(result => {
                    if (result.success) {
                        resetCount++;
                    } else {
                        errorCount++;
                        console.error(`Failed to reset currency for player ${result.playerId}: ${result.error}`);
                    }
                });
            }

            await player.pm(`Currency reset complete.`);

        } catch (error) {
            if (error instanceof TakaroUserError) {
                throw error;
            }
            throw new TakaroUserError(`Failed to reset currency: ${error.message}`);
        }
    }
    // 2. Delete teleport AND waypoint variables (treating them as one system)
    if ((wipeAll && mod.userConfig.wipeTeleports) || args.target === 'teleports') {
        try {
            await player.pm("Wiping teleports and waypoints data...");

            let totalDeleted = 0;

            // Find teleports module
            const teleportsModule = await takaro.module.moduleControllerSearch({
                filters: {
                    name: ['teleports']
                }
            });
            const teleportsModuleId = teleportsModule.data.data.length > 0 ? teleportsModule.data.data[0].id : null;

            // Find waypoints module (if it exists as a separate module)
            const waypointsModule = await takaro.module.moduleControllerSearch({
                filters: {
                    name: ['Waypoints']
                }
            });
            const waypointsModuleId = waypointsModule.data.data.length > 0 ? waypointsModule.data.data[0].id : null;

            // Handle teleport variables
            if (teleportsModuleId) {
                const teleportVars = await takaro.variable.variableControllerSearch({
                    filters: {
                        gameServerId: [gameServerId],
                        moduleId: [teleportsModuleId]
                    },
                    limit: 500
                });

                // Delete variables in batches
                const batchSize = 10;
                for (let i = 0; i < teleportVars.data.data.length; i += batchSize) {
                    const batch = teleportVars.data.data.slice(i, i + batchSize);
                    await Promise.all(
                        batch.map(variable =>
                            takaro.variable.variableControllerDelete(variable.id)
                                .then(() => { totalDeleted++; })
                                .catch(err => console.error(`Failed to delete variable ${variable.id}: ${err.message}`))
                        )
                    );
                }
            }

            // Handle waypoint variables if they exist as a separate module
            if (waypointsModuleId) {
                const waypointVars = await takaro.variable.variableControllerSearch({
                    filters: {
                        gameServerId: [gameServerId],
                        moduleId: [waypointsModuleId]
                    },
                    limit: 500
                });

                // Delete variables in batches
                const batchSize = 10;
                for (let i = 0; i < waypointVars.data.data.length; i += batchSize) {
                    const batch = waypointVars.data.data.slice(i, i + batchSize);
                    await Promise.all(
                        batch.map(variable =>
                            takaro.variable.variableControllerDelete(variable.id)
                                .then(() => { totalDeleted++; })
                                .catch(err => console.error(`Failed to delete variable ${variable.id}: ${err.message}`))
                        )
                    );
                }
            }

            await player.pm(`Deleted ${totalDeleted} teleport and waypoint variables from the server.`);
        } catch (error) {
            await player.pm(`Error wiping teleports and waypoints: ${error.message}`);
        }
    }

    // 3. Wipe starter kit claims if specified and enabled in config
    if ((wipeAll && mod.userConfig.wipeStarterKitClaims) || args.target === 'starterkits') {
        try {
            await player.pm("Resetting all starter kit claims...");

            // Search for variables with the exact starterkit lock key
            const starterKitVars = await takaro.variable.variableControllerSearch({
                filters: {
                    gameServerId: [gameServerId],
                    key: ['t_starterkit_lock']
                }
            });

            const claimCount = starterKitVars.data.data.length;

            // Delete all found starterkit lock variables
            for (const variable of starterKitVars.data.data) {
                await takaro.variable.variableControllerDelete(variable.id);
            }

            await player.pm(`Starter kit claims reset complete. Reset ${claimCount} claims.`);
        } catch (error) {
            throw new TakaroUserError(`Failed to reset starter kit claims: ${error.message}`);
        }
    }


    // 4. Broadcast the wipe announcement to all players if configured
    if (mod.userConfig.globalAnnouncement && mod.userConfig.globalAnnouncement.length > 0) {
        try {
            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                message: mod.userConfig.globalAnnouncement
            });
        } catch (error) {
            await player.pm(`Error sending global announcement: ${error.message}`);
        }
    }

    // Confirm completion to the admin
    await player.pm('Server wipe completed successfully.');
}

await main();

