import { takaro, data, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { player, gameServerId, module: mod, chatMessage } = data;

    // Get the command prefix and extract full answer from raw message
    const prefix = (await takaro.settings.settingsControllerGetOne('commandPrefix', gameServerId)).data.data.value;
    const messageWithoutPrefix = chatMessage.msg.slice(prefix.length).trim();
    const commandParts = messageWithoutPrefix.split(' ');

    if (commandParts.length < 2) {
        throw new TakaroUserError('Please provide an answer!');
    }

    const fullAnswer = commandParts.slice(1).join(' ').trim();

    if (!fullAnswer || fullAnswer === '') {
        throw new TakaroUserError('Please provide an answer!');
    }

    // Check if there's an active trivia question
    const questionVariable = await takaro.variable.variableControllerSearch({
        filters: {
            key: ['trivia_question'],
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId]
        }
    });

    if (questionVariable.data.data.length === 0) {
        throw new TakaroUserError('No active trivia question! Wait for the next one.');
    }

    // Get the correct answer
    const answerVariable = await takaro.variable.variableControllerSearch({
        filters: {
            key: ['trivia_answer'],
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId]
        }
    });

    if (answerVariable.data.data.length === 0) {
        throw new TakaroUserError('No answer found for the current question.');
    }

    // Get reward info
    const rewardVariable = await takaro.variable.variableControllerSearch({
        filters: {
            key: ['trivia_reward'],
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId]
        }
    });

    if (rewardVariable.data.data.length === 0) {
        throw new TakaroUserError('No reward configured for this trivia.');
    }

    const correctAnswer = answerVariable.data.data[0].value;
    const playerAnswer = fullAnswer; // Use the full answer we extracted

    // Normalize answers for comparison (lowercase, remove extra spaces)
    const normalizeAnswer = (answer) => {
        return answer.toLowerCase().replace(/\s+/g, ' ').trim();
    };

    const normalizedCorrectAnswer = normalizeAnswer(correctAnswer);
    const normalizedPlayerAnswer = normalizeAnswer(playerAnswer);

    console.log(`Player ${player.name} answered: "${playerAnswer}" (normalized: "${normalizedPlayerAnswer}")`);
    console.log(`Correct answer: "${correctAnswer}" (normalized: "${normalizedCorrectAnswer}")`);

    // Check if the answer is correct
    if (normalizedPlayerAnswer === normalizedCorrectAnswer) {
        // Give reward
        const rewardData = JSON.parse(rewardVariable.data.data[0].value);

        let rewardMessage = '';

        if (rewardData.type === 'currency') {
            await takaro.playerOnGameserver.playerOnGameServerControllerAddCurrency(gameServerId, player.id, {
                currency: rewardData.amount
            });

            const currencyName = (await takaro.settings.settingsControllerGetOne('currencyName', gameServerId)).data.data.value;
            rewardMessage = `You won ${rewardData.amount} ${currencyName}!`;

        } else if (rewardData.type === 'items') {
            const item = (await takaro.item.itemControllerFindOne(rewardData.itemId)).data.data;

            await takaro.gameserver.gameServerControllerGiveItem(gameServerId, player.id, {
                name: item.code,
                amount: rewardData.amount,
                quality: rewardData.quality || ''
            });

            rewardMessage = `You won ${rewardData.amount}x ${item.name}!`;
        }

        // WIN TRACKING - Record the player's win
        const winKey = `trivia_wins_${player.id}`;
        const existingWins = await takaro.variable.variableControllerSearch({
            filters: {
                key: [winKey],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId]
            }
        });

        let totalWins = 1;
        if (existingWins.data.data.length > 0) {
            totalWins = parseInt(existingWins.data.data[0].value) + 1;
            await takaro.variable.variableControllerUpdate(existingWins.data.data[0].id, {
                value: totalWins.toString()
            });
        } else {
            await takaro.variable.variableControllerCreate({
                key: winKey,
                value: '1',
                gameServerId,
                moduleId: mod.moduleId,
                playerId: player.id
            });
        }

        // Success message with total wins
        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: `ðŸŽ‰ ${player.name} got it right! The answer was: ${correctAnswer}. ${rewardMessage} (Total wins: ${totalWins})`
        });

        // Clean up trivia variables
        const variablesToClean = await takaro.variable.variableControllerSearch({
            filters: {
                key: ['trivia_question', 'trivia_answer', 'trivia_reward', 'trivia_type', 'trivia_incorrect_answers'],
                gameServerId: [gameServerId],
                moduleId: [mod.moduleId]
            }
        });

        await Promise.all(variablesToClean.data.data.map(variable =>
            takaro.variable.variableControllerDelete(variable.id)
        ));

        console.log(`${player.name} answered correctly and won! Total wins: ${totalWins}`);

    } else {
        // Wrong answer
        await player.pm(`âŒ Sorry, "${playerAnswer}" is not correct. Try again!`);
        console.log(`${player.name} answered incorrectly.`);
    }
}

await main();

import { takaro, data, checkPermission, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { gameServerId, module: mod, pog, player } = data;

    // Check permission
    if (!checkPermission(pog, 'TRIVIA_RESET_LEADERBOARD')) {
        throw new TakaroUserError('You do not have permission to reset the trivia leaderboard.');
    }

    // Get all trivia-related variables for this server
    const triviaVariables = await takaro.variable.variableControllerSearch({
        filters: {
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId]
        },
        search: {
            key: ['trivia_wins_', 'trivia_question', 'trivia_answer', 'trivia_reward', 'trivia_type', 'trivia_incorrect_answers']
        },
        limit: 1000
    });

    if (triviaVariables.data.data.length === 0) {
        await player.pm('ðŸ—‘ï¸ No trivia data found to reset.');
        return;
    }

    // Delete all trivia variables
    const deletePromises = triviaVariables.data.data.map(variable =>
        takaro.variable.variableControllerDelete(variable.id)
    );

    await Promise.allSettled(deletePromises);

    // Send confirmation
    const winsCount = triviaVariables.data.data.filter(v => v.key.startsWith('trivia_wins_')).length;

    await player.pm(`ðŸ—‘ï¸ Trivia leaderboard reset successfully! Cleared ${winsCount} player records.`);

    // Announce to server
    await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
        message: `ðŸ”„ Trivia leaderboard has been reset by an administrator!`
    });

    console.log(`Trivia leaderboard reset by player ${player.name} (${player.id})`);
}

await main();

import { takaro, data } from '@takaro/helpers';

async function main() {
    const { gameServerId, module: mod, player } = data;

    // Get all trivia wins for this server
    const allWins = await takaro.variable.variableControllerSearch({
        filters: {
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId]
        },
        search: {
            key: ['trivia_wins_']
        },
        limit: 1000
    });

    if (allWins.data.data.length === 0) {
        await player.pm('ðŸ† No trivia winners yet! Be the first to answer a question correctly!');
        return;
    }

    // Sort by wins (stored as string, so convert to number)
    const sortedWins = allWins.data.data
        .map(record => ({
            playerId: record.playerId,
            wins: parseInt(record.value) || 0
        }))
        .sort((a, b) => b.wins - a.wins)
        .slice(0, mod.userConfig.leaderboardSize || 10);

    // Get player names
    const leaderboard = await Promise.all(
        sortedWins.map(async (record, index) => {
            const playerData = await takaro.player.playerControllerGetOne(record.playerId);
            return {
                position: index + 1,
                name: playerData.data.data.name,
                wins: record.wins
            };
        })
    );

    // Build leaderboard message
    let message = 'ðŸ† **Trivia Leaderboard** ðŸ§ \n\n';

    leaderboard.forEach(entry => {
        const medal = entry.position === 1 ? 'ðŸ¥‡' : entry.position === 2 ? 'ðŸ¥ˆ' : entry.position === 3 ? 'ðŸ¥‰' : '  ';
        message += `${medal} ${entry.position}. ${entry.name} - ${entry.wins} win${entry.wins !== 1 ? 's' : ''}\n`;
    });

    // Add current player's ranking if not in top list
    const currentPlayerWins = sortedWins.find(record => record.playerId === player.id);
    if (!currentPlayerWins && allWins.data.data.some(record => record.playerId === player.id)) {
        const playerRecord = allWins.data.data.find(record => record.playerId === player.id);
        const playerWins = parseInt(playerRecord.value) || 0;
        const playerRank = allWins.data.data
            .map(record => parseInt(record.value) || 0)
            .sort((a, b) => b - a)
            .findIndex(wins => wins <= playerWins) + 1;

        message += `\nðŸ“ Your rank: #${playerRank} with ${playerWins} win${playerWins !== 1 ? 's' : ''}`;
    }

    await player.pm(message);
}

await main();

