import { takaro, data } from '@takaro/helpers';

async function main() {
    const { gameServerId, module: mod } = data;

    // Check for online players
    const currentPlayersRes = await takaro.playerOnGameserver.playerOnGameServerControllerSearch({
        filters: {
            gameServerId: [gameServerId],
            online: [true]
        }
    });

    if (currentPlayersRes.data.data.length === 0) {
        console.log('No online players, skipping trivia');
        return;
    }

    console.log(`Found ${currentPlayersRes.data.data.length} online players`);

    // Clear existing variables first
    const existingVariables = await takaro.variable.variableControllerSearch({
        filters: {
            key: ['trivia_question', 'trivia_answer', 'trivia_reward', 'trivia_type', 'trivia_incorrect_answers'],
            gameServerId: [gameServerId],
            moduleId: [mod.moduleId]
        }
    });

    await Promise.all(existingVariables.data.data.map(variable =>
        takaro.variable.variableControllerDelete(variable.id)
    ));

    let questionData = null;
    let questionSource = mod.userConfig.questionSource || 'api';

    if (questionSource === 'api') {
        console.log('Attempting to fetch from Open Trivia Database API using Axios');

        try {
            // Check if axios is available
            if (takaro.axios) {
                console.log('Axios is available! Making API request...');

                // Build API URL
                let url = 'https://opentdb.com/api.php?amount=1';

                // Add categories if specified
                const categories = mod.userConfig.apiCategory || ['any'];
                if (categories.length > 0 && !categories.includes('any')) {
                    const categoryMap = {
                        'general_knowledge': 9, 'books': 10, 'film': 11, 'music': 12,
                        'musicals_theatres': 13, 'television': 14, 'video_games': 15,
                        'board_games': 16, 'science_nature': 17, 'computers': 18,
                        'mathematics': 19, 'mythology': 20, 'sports': 21, 'geography': 22,
                        'history': 23, 'politics': 24, 'art': 25, 'celebrities': 26,
                        'animals': 27, 'vehicles': 28, 'comics': 29, 'gadgets': 30,
                        'anime_manga': 31, 'cartoon_animations': 32
                    };

                    const randomCategory = categories[Math.floor(Math.random() * categories.length)];
                    const categoryId = categoryMap[randomCategory];
                    if (categoryId) {
                        url += `&category=${categoryId}`;
                    }
                }

                // Add difficulty if specified
                if (mod.userConfig.apiDifficulty && mod.userConfig.apiDifficulty !== 'any') {
                    url += `&difficulty=${mod.userConfig.apiDifficulty}`;
                }

                // Add type if specified
                if (mod.userConfig.apiType && mod.userConfig.apiType !== 'any') {
                    url += `&type=${mod.userConfig.apiType}`;
                }

                console.log('Fetching trivia question from:', url);

                // Make the API request using Axios
                const response = await takaro.axios.get(url);
                const apiData = response.data;

                console.log('API Response:', JSON.stringify(apiData));

                if (apiData.response_code === 0 && apiData.results && apiData.results.length > 0) {
                    const question = apiData.results[0];

                    questionData = {
                        question: decodeHtmlEntities(question.question),
                        answer: decodeHtmlEntities(question.correct_answer),
                        type: question.type,
                        incorrectAnswers: question.incorrect_answers ? question.incorrect_answers.map(decodeHtmlEntities) : []
                    };
                    console.log('Successfully fetched API question:', questionData.question);
                } else {
                    console.log('API returned error or no results:', apiData);
                    questionSource = 'custom';
                }
            } else {
                console.log('Axios not available, falling back to custom questions');
                questionSource = 'custom';
            }
        } catch (error) {
            console.log('API failed, falling back to custom questions:', error.message);
            questionSource = 'custom';
        }
    }

    if (questionSource === 'custom' || !questionData) {
        console.log('Using custom questions');
        const questions = mod.userConfig.questions || [
            { question: "What is the largest country in the world?", answer: "Russia" },
            { question: "What is the capital of France?", answer: "Paris" },
            { question: "What is 2 + 2?", answer: "4" }
        ];

        const randomQuestion = questions[Math.floor(Math.random() * questions.length)];
        questionData = {
            question: randomQuestion.question,
            answer: randomQuestion.answer,
            type: 'multiple',
            incorrectAnswers: []
        };
    }

    if (!questionData) {
        console.log('No question data available');
        return;
    }

    // Store question data
    await takaro.variable.variableControllerCreate({
        key: 'trivia_question',
        value: questionData.question,
        gameServerId,
        moduleId: mod.moduleId
    });

    await takaro.variable.variableControllerCreate({
        key: 'trivia_answer',
        value: questionData.answer,
        gameServerId,
        moduleId: mod.moduleId
    });

    await takaro.variable.variableControllerCreate({
        key: 'trivia_type',
        value: questionData.type,
        gameServerId,
        moduleId: mod.moduleId
    });

    if (questionData.incorrectAnswers.length > 0) {
        await takaro.variable.variableControllerCreate({
            key: 'trivia_incorrect_answers',
            value: JSON.stringify(questionData.incorrectAnswers),
            gameServerId,
            moduleId: mod.moduleId
        });
    }

    // Setup reward - Updated to handle "both" option
    const rewardType = mod.userConfig.rewardType || 'currency';
    let selectedRewardType = rewardType;

    // If "both" is selected, randomly choose between currency and items
    if (rewardType === 'both') {
        selectedRewardType = Math.random() < 0.5 ? 'currency' : 'items';
    }

    if (selectedRewardType === 'items') {
        const configuredItems = mod.userConfig.rewardItems || [];
        if (configuredItems.length > 0) {
            const randomItemIndex = Math.floor(Math.random() * configuredItems.length);
            const selectedItem = configuredItems[randomItemIndex];

            await takaro.variable.variableControllerCreate({
                key: 'trivia_reward',
                value: JSON.stringify({
                    type: 'items',
                    itemId: selectedItem.item,
                    amount: selectedItem.amount || 1,
                    quality: selectedItem.quality || ''
                }),
                gameServerId,
                moduleId: mod.moduleId
            });
        } else {
            console.error('Items reward type selected but no items configured, falling back to currency');
            await takaro.variable.variableControllerCreate({
                key: 'trivia_reward',
                value: JSON.stringify({
                    type: 'currency',
                    amount: mod.userConfig.rewardAmount || 100
                }),
                gameServerId,
                moduleId: mod.moduleId
            });
        }
    } else {
        // Currency reward
        await takaro.variable.variableControllerCreate({
            key: 'trivia_reward',
            value: JSON.stringify({
                type: 'currency',
                amount: mod.userConfig.rewardAmount || 100
            }),
            gameServerId,
            moduleId: mod.moduleId
        });
    }
    const prefix = (await takaro.settings.settingsControllerGetOne('commandPrefix', gameServerId)).data.data.value;

    // Create appropriate message based on question type
    let message = `ðŸ§  Trivia Time! ${questionData.question}`;

    if (questionData.type === 'boolean') {
        message += ` (Answer with ${prefix}answer true or ${prefix}answer false)`;
    } else if (questionData.incorrectAnswers.length > 0) {
        const allAnswers = [questionData.answer, ...questionData.incorrectAnswers]
            .sort(() => Math.random() - 0.5);

        message += `\nOptions: ${allAnswers.join(', ')} (Answer with ${prefix}answer <your choice>)`;
    } else {
        message += ` (Answer with ${prefix}answer <your guess>)`;
    }

    await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
        message: message
    });

    console.log('Trivia question posted successfully');
}

function decodeHtmlEntities(text) {
    if (!text) return text;

    const entityMap = {
        '&amp;': '&', '&lt;': '<', '&gt;': '>', '&quot;': '"', '&#039;': "'", '&apos;': "'",
        '&nbsp;': ' ', '&eacute;': 'Ã©', '&egrave;': 'Ã¨', '&ecirc;': 'Ãª', '&euml;': 'Ã«',
        '&aacute;': 'Ã¡', '&agrave;': 'Ã ', '&acirc;': 'Ã¢', '&auml;': 'Ã¤',
        '&iacute;': 'Ã­', '&igrave;': 'Ã¬', '&icirc;': 'Ã®', '&iuml;': 'Ã¯',
        '&oacute;': 'Ã³', '&ograve;': 'Ã²', '&ocirc;': 'Ã´', '&ouml;': 'Ã¶',
        '&uacute;': 'Ãº', '&ugrave;': 'Ã¹', '&ucirc;': 'Ã»', '&uuml;': 'Ã¼',
        '&ntilde;': 'Ã±', '&ccedil;': 'Ã§'
    };

    let decoded = text;
    for (const [entity, char] of Object.entries(entityMap)) {
        decoded = decoded.replace(new RegExp(entity, 'g'), char);
    }

    decoded = decoded.replace(/&#(\d+);/g, (match, num) => {
        return String.fromCharCode(parseInt(num, 10));
    });

    return decoded;
}

await main();

