        console.log('No question data available');
        return;
    }

    // Store question data
    await takaro.variable.variableControllerCreate({
        key: 'trivia_question',
        value: questionData.question,
        gameServerId,
        moduleId: mod.moduleId
    });

    await takaro.variable.variableControllerCreate({
        key: 'trivia_answer',
        value: questionData.answer,
        gameServerId,
        moduleId: mod.moduleId
    });

    await takaro.variable.variableControllerCreate({
        key: 'trivia_type',
        value: questionData.type,
        gameServerId,
        moduleId: mod.moduleId
    });

    if (questionData.incorrectAnswers.length > 0) {
        await takaro.variable.variableControllerCreate({
            key: 'trivia_incorrect_answers',
            value: JSON.stringify(questionData.incorrectAnswers),
            gameServerId,
            moduleId: mod.moduleId
        });
    }

    // Setup reward - Updated to handle "both" option
    const rewardType = mod.userConfig.rewardType || 'currency';
    let selectedRewardType = rewardType;

    // If "both" is selected, randomly choose between currency and items
    if (rewardType === 'both') {
        selectedRewardType = Math.random() < 0.5 ? 'currency' : 'items';
    }

    if (selectedRewardType === 'items') {
        const configuredItems = mod.userConfig.rewardItems || [];
        if (configuredItems.length > 0) {
            const randomItemIndex = Math.floor(Math.random() * configuredItems.length);
            const selectedItem = configuredItems[randomItemIndex];

            await takaro.variable.variableControllerCreate({
                key: 'trivia_reward',
                value: JSON.stringify({
                    type: 'items',
                    itemId: selectedItem.item,
                    amount: selectedItem.amount || 1,
                    quality: selectedItem.quality || ''
                }),
                gameServerId,
                moduleId: mod.moduleId
            });
        } else {
            console.error('Items reward type selected but no items configured, falling back to currency');
            await takaro.variable.variableControllerCreate({
                key: 'trivia_reward',
                value: JSON.stringify({
                    type: 'currency',
                    amount: mod.userConfig.rewardAmount || 100
                }),
                gameServerId,
                moduleId: mod.moduleId
            });
        }
    } else {
        // Currency reward
        await takaro.variable.variableControllerCreate({
            key: 'trivia_reward',
            value: JSON.stringify({
                type: 'currency',
                amount: mod.userConfig.rewardAmount || 100
            }),
            gameServerId,
            moduleId: mod.moduleId
        });
    }
    const prefix = (await takaro.settings.settingsControllerGetOne('commandPrefix', gameServerId)).data.data.value;

    // Create appropriate message based on question type
    let message = `ðŸ§  Trivia Time! ${questionData.question}`;

    if (questionData.type === 'boolean') {
        message += ` (Answer with ${prefix}answer true or ${prefix}answer false)`;
    } else if (questionData.incorrectAnswers.length > 0) {
        const allAnswers = [questionData.answer, ...questionData.incorrectAnswers]
            .sort(() => Math.random() - 0.5);

        message += `\nOptions: ${allAnswers.join(', ')} (Answer with ${prefix}answer <your choice>)`;
    } else {
        message += ` (Answer with ${prefix}answer <your guess>)`;
    }

    await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
        message: message
    });

    console.log('Trivia question posted successfully');
}

function decodeHtmlEntities(text) {
    if (!text) return text;

    const entityMap = {
        '&amp;': '&', '&lt;': '<', '&gt;': '>', '&quot;': '"', '&#039;': "'", '&apos;': "'",
        '&nbsp;': ' ', '&eacute;': 'Ã©', '&egrave;': 'Ã¨', '&ecirc;': 'Ãª', '&euml;': 'Ã«',
        '&aacute;': 'Ã¡', '&agrave;': 'Ã ', '&acirc;': 'Ã¢', '&auml;': 'Ã¤',
        '&iacute;': 'Ã­', '&igrave;': 'Ã¬', '&icirc;': 'Ã®', '&iuml;': 'Ã¯',
        '&oacute;': 'Ã³', '&ograve;': 'Ã²', '&ocirc;': 'Ã´', '&ouml;': 'Ã¶',
        '&uacute;': 'Ãº', '&ugrave;': 'Ã¹', '&ucirc;': 'Ã»', '&uuml;': 'Ã¼',
        '&ntilde;': 'Ã±', '&ccedil;': 'Ã§'
    };

    let decoded = text;
    for (const [entity, char] of Object.entries(entityMap)) {
        decoded = decoded.replace(new RegExp(entity, 'g'), char);
    }

    decoded = decoded.replace(/&#(\d+);/g, (match, num) => {
        return String.fromCharCode(parseInt(num, 10));
    });

    return decoded;
}

