import { data, takaro } from '@takaro/helpers';

async function main() {
    const logMessage = data.eventData.msg;
    const regex = /Entity bossShocker.*?killed by (.+?) \d+/;
    const matches = logMessage.match(regex);

    const gameServerId = data.gameServerId;
    const playerName = matches[1].trim(); // Use trim() to remove any trailing space.

    const amountx = data.module.userConfig.coinAmountBoss;
    const message = data.module.userConfig.announceShocker;

    const formattedMessage = message
        .replace('{pname}', playerName)
        .replace('{amount}', amountx);

    await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
        message: formattedMessage
    });

    const { data: searchResults } = await takaro.player.playerControllerSearch({
        search: { name: playerName },
    });

    const foundPlayer = searchResults.data[0];

    await takaro.playerOnGameserver.playerOnGameServerControllerAddCurrency(
        gameServerId,
        foundPlayer.id,
        {
            currency: amountx
        }
    );
}

await main();

