import { takaro, data, checkPermission, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { player, module: mod, gameServerId, pog } = data;

    //Check Permissions
    if (!checkPermission(pog, 'ZOMB_FREE_SELF')) {
        throw new TakaroUserError('You do not have permission to use the [02FEDC]Zombie free[-] commmand.');
    };

    await player.pm(`[EA899A]Zombie Free[-] [ADDFB3]for you![-]`);

    //Execute dm mode
    await takaro.gameserver.gameServerControllerExecuteCommand(data.gameServerId, {
        command: `ccc radius 30 ${player.name} VIP_${player.name}_HF 0 hostilefree`,
    });

    //ccc radius 5 YOURNAME jail 0 reversed)
    //ccc radius < radius > <steamId/entityId/Name > <claimid/steamid> <accessLevel> [<type>]

    // Execute the command
    await takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
        command: `ccc radius 30 ${player.name} VIP_${player.name}_Normal 400`
    });

    // Search for the adminMode variable
    const zombFreeVariable = (await takaro.variable.variableControllerSearch({
        filters: {
            key: ['zombFreeVariable'],
            moduleId: [mod.moduleId],
            playerId: [player.id],
            gameServerId: [gameServerId],
        },
    })).data.data[0];

    // Update the last message variable so the next time this cron job runs, we know what to send
    if (zombFreeVariable) {
        // The variable already exists, update it
        const currentValue = parseInt(zombFreeVariable.value, 10); // Ensure it's an integer
        await takaro.variable.variableControllerUpdate(zombFreeVariable.id, {
            value: (currentValue + 1).toString(), // Increment and convert to string
        });
    } else {
        // The variable doesn't exist, create it with a starting value of 0
        await takaro.variable.variableControllerCreate({
            key: 'zombFreeVariable',
            value: '0', // Start with 0 as a string
            playerId: player.id,
            moduleId: mod.moduleId,
            gameServerId: gameServerId,
        });
    }
}
await main();

import { takaro, data, checkPermission, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { player, gameServerId, pog, module: mod, arguments: args } = data;
    const playerTarget = args.playerTarget;

    // Check permissions first
    if (!checkPermission(pog, 'STAFF_COMMANDS')) {
        throw new TakaroUserError('You do not have permission to use [02FEDC]Moderator[-] commands.');
    }

    // Check if there's an existing confirmation variable
    const confirmationKey = `kill_confirmation_${playerTarget}`;
    const existingConfirmation = await takaro.variable.variableControllerSearch({
        filters: {
            key: [confirmationKey],
            gameServerId: [gameServerId],
            playerId: [pog.playerId],
            moduleId: [mod.moduleId]
        }
    });

    // If confirmation variable exists, proceed with kill
    if (existingConfirmation.data.data.length > 0) {
        // Delete the confirmation variable first
        await takaro.variable.variableControllerDelete(existingConfirmation.data.data[0].id);

        const targetArg = /\s/.test(playerTarget) ? `"${playerTarget}"` : playerTarget;

        // Execute the kill command
        const response = (await takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
            command: `kill ${targetArg}`,
        })).data.data.rawResult;

        if (response.includes('Targetplayer is offline')) {
            throw new TakaroUserError(`[02FEDC]${playerTarget}[-] is not online`);
        }

        await player.pm(`You have killed [02FEDC]${playerTarget}[-].`);
    } else {
        // Create confirmation variable with 30 second expiry
        const now = new Date();
        const expiry = new Date(now.getTime() + 30 * 1000); // 30 seconds from now

        await takaro.variable.variableControllerCreate({
            key: confirmationKey,
            value: JSON.stringify({ expiry: expiry.toISOString() }),
            gameServerId,
            moduleId: mod.moduleId,
            playerId: pog.playerId
        });

        // Send confirmation message
        await player.pm(`Are you sure you want to kill [02FEDC]${playerTarget}[-]? Run the command again within 30 seconds to confirm.`);
    }
}

await main();

