import { takaro, data, checkPermission, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { player, gameServerId, pog, arguments: args } = data;
    const { person, reasoning } = args;
    const discordChannel = data.module.userConfig.userDiscordChannel;
    if (!checkPermission(pog, 'MUTE')) {
        throw new TakaroUserError('You do not have permission to use [02FEDC]Moderator[-] commmands.');
    }

    const targetArg = /\s/.test(person) ? `"${person}"` : person;


    const response = (await takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
        command: `mcp ${targetArg} true`,
    })).data.data.rawResult;

    if (response.includes('is not a valid entity id, player name or user id')) {
        throw new TakaroUserError(`[02FEDC]${person}[-]'s name wasn't spelled correctly!`);
    }

    //Confirmation
    await player.pm(`[02FEDC]${person}[-] was muted.`);

    await takaro.discord.discordControllerSendMessage(discordChannel, {
        message: `🤐 ${player.name} muted ${person} for ${reasoning}.`,
    });
}

await main();

import { takaro, data, checkPermission, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { player, module: mod, gameServerId, pog } = data;

    //Check Permissions
    if (!checkPermission(pog, 'ZOMB_FREE_SELF')) {
        throw new TakaroUserError('You do not have permission to use the [02FEDC]Zombie free[-] commmand.');
    };

    await player.pm(`[EA899A]Zombie Free[-] [ADDFB3]for you![-]`);

    //Execute dm mode
    await takaro.gameserver.gameServerControllerExecuteCommand(data.gameServerId, {
        command: `ccc radius 40 EOS_${player.epicOnlineServicesId} "VIP_${player.name}_HF" 0 hostilefree`,
    });

    //ccc radius 5 YOURNAME jail 0 reversed)
    //ccc radius < radius > <steamId/entityId/Name > <claimid/steamid> <accessLevel> [<type>]

    // Execute the command
    await takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
        command: `ccc radius 40 EOS_${player.epicOnlineServicesId} "VIP_${player.name}_Normal" 400`
    });

    // Search for the adminMode variable
    const zombFreeVariable = (await takaro.variable.variableControllerSearch({
        filters: {
            key: ['zombFreeVariable'],
            moduleId: [mod.moduleId],
            playerId: [player.id],
            gameServerId: [gameServerId],
        },
    })).data.data[0];

    // Update the last message variable so the next time this cron job runs, we know what to send
    if (zombFreeVariable) {
        // The variable already exists, update it
        const currentValue = parseInt(zombFreeVariable.value, 10); // Ensure it's an integer
        await takaro.variable.variableControllerUpdate(zombFreeVariable.id, {
            value: (currentValue + 1).toString(), // Increment and convert to string
        });
    } else {
        // The variable doesn't exist, create it with a starting value of 0
        await takaro.variable.variableControllerCreate({
            key: 'zombFreeVariable',
            value: '0', // Start with 0 as a string
            playerId: player.id,
            moduleId: mod.moduleId,
            gameServerId: gameServerId,
        });
    }
}
await main();

