import { takaro, data, checkPermission, TakaroUserError } from '@takaro/helpers';

async function extractPermissionIds() {
    const pog = data;
    if (!checkPermission(pog, 'STAFF_COMMANDS')) {
        throw new TakaroUserError('You do not have permission to use [02FEDC]Moderator[-] commmands.');
    }

    try {
        // Try using direct search which seems more reliable based on the logs
        const directResponse = await takaro.role.roleControllerSearch({
            filters: {
                id: ['3ca7484e-5656-4474-897e-db149b732273']
            }
        });

        // Check if we got role data
        if (directResponse.data &&
            directResponse.data.data &&
            directResponse.data.data.length > 0 &&
            directResponse.data.data[0].permissions) {

            const permissions = directResponse.data.data[0].permissions;

            // Extract all permission IDs into an array
            const permissionIds = permissions
                .filter(permData => permData.permission && permData.permission.id)
                .map(permData => permData.permission.id);

            // Output all IDs as a single array on one line
            console.log(permissionIds);

            return permissionIds.length;
        }

        console.log([]);
        return 0;

    } catch (error) {
        console.error(`Error extracting permission IDs: ${error.message}`);
        console.log([]);
        return 0;
    }
}

// Execute the function
async function main() {
    try {
        await extractPermissionIds();
    } catch (error) {
        console.log([]);
    }
}



