    //Execute Command arrest
    const response = (await takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
        command: `arrest ${targetArg}`,
    })).data.data.rawResult;

    //Throw an error if the name is wrong
    if (response.includes('Player not found!')) {
        throw new TakaroUserError(`[8BD3E6]${playerTarget}'s[-] [E9EC6B]name wasn't spelled correctly! Use quotes for names with spaces. Copy their name from Discord if you cannot type their name.[-]`);
    }

    //Send global message in game to shame the offender
    await takaro.gameserver.gameServerControllerSendMessage(data.gameServerId, {
        message: `[B1A2CA]${playerTarget}[-] [EA899A]was arrested for ${reasoning}[-]`,
    });
    //Discord Channel Configuration
    const userDiscord = data.module.userConfig.userDiscordChannel;
    //Send it to Discord
    await takaro.discord.discordControllerSendMessage(userDiscord, {
        message: `${player.name} arrested ${playerTarget} for ${reasoning}`,
    });
};
