import { takaro, data } from '@takaro/helpers';

async function main() {
    const adminCommandDiscord = data.module.userConfig.adminCommandDiscord;
    const modId = data.module.module.id;
    const moduleId = data.module.moduleId;

    const res = await takaro.module.moduleControllerGetOne(modId);
    const commands = res.data.data.latestVersion.commands;

    // Sort the commands array alphabetically by the trigger name.
    commands.sort((a, b) => a.trigger.localeCompare(b.trigger));

    // Filter out duplicate commands based on the trigger name.
    const seen = new Set();
    const uniqueCommands = commands.filter(command => {
        const duplicate = seen.has(command.trigger);
        seen.add(command.trigger);
        return !duplicate;
    });

    // Get today's date and format it.
    const today = new Date();
    const dateOptions = { timeZone: 'America/Chicago', year: 'numeric', month: 'long', day: 'numeric' };
    const formattedDate = today.toLocaleDateString('en-US', dateOptions);

    const embedDescriptionLimit = 4096;
    const embeds = [];

    // Build command list as description text
    let currentDescription = `Help is available in game! Type \`/admin\` or \`/admin <command>\` for specific help.\n\n`;

    for (const command of uniqueCommands) {
        const cleanedHelpText = command.helpText
            .replace(/\[[0-9A-Fa-f]{6}\]/g, '')
            .replace(/\[-\]/g, '');

        const commandText = `**${command.trigger}**\n- ${cleanedHelpText.trim() || 'No description'}\n\n`;

        // Check if adding this command would exceed the description limit
        if (currentDescription.length + commandText.length > embedDescriptionLimit) {
            // Create embed with current description
            embeds.push({
                title: embeds.length === 0 ? `✦━━━━━ Admin Command List ━━━━━✦` : `✦━━━━━ Admin Command List (continued) ━━━━━✦`,
                description: currentDescription.trim(),
                color: 0x00AE86,
                footer: {
                    text: `Last Updated: ${formattedDate}`
                }
            });

            // Start new description
            currentDescription = commandText;
        } else {
            currentDescription += commandText;
        }
    }

    // Add the last embed
    if (currentDescription.trim().length > 0) {
        embeds.push({
            title: embeds.length === 0 ? `✦━━━━━ Admin Command List ━━━━━✦` : `✦━━━━━ Admin Command List (continued) ━━━━━✦`,
            description: currentDescription.trim(),
            color: 0x00AE86,
            footer: {
                text: `Last Updated: ${formattedDate}`
            }
        });
    }

    // Search for existing message ID variables
    const existingVariables = await takaro.variable.variableControllerSearch({
        filters: {
            key: embeds.map((_, index) => `discord_admin_commands_msg_${index}`),
            moduleId: [moduleId]
        }
    });

    const existingVarMap = new Map();
    if (existingVariables.data.data) {
        for (const variable of existingVariables.data.data) {
            existingVarMap.set(variable.key, variable.value);
        }
    }

    // Process each embed
    for (let i = 0; i < embeds.length; i++) {
        const embed = embeds[i];
        const varKey = `discord_admin_commands_msg_${i}`;
        const existingMessageId = existingVarMap.get(varKey);

        if (existingMessageId) {
            // Update existing message
            try {
                await takaro.discord.discordControllerUpdateMessage(adminCommandDiscord, existingMessageId, {
                    embed: embed
                });
            } catch (error) {
                // If update fails (e.g., message was deleted), create a new message
                const createRes = await takaro.discord.discordControllerSendMessage(adminCommandDiscord, {
                    embed: embed
                });
                // Update the variable with the new message ID
                const varSearch = await takaro.variable.variableControllerSearch({
                    filters: {
                        key: [varKey],
                        moduleId: [moduleId]
                    }
                });
                if (varSearch.data.data && varSearch.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(varSearch.data.data[0].id, {
                        value: createRes.data.data.id
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: varKey,
                        value: createRes.data.data.id,
                        moduleId: moduleId
                    });
                }
            }
        } else {
            // Create new message and store the ID
            const createRes = await takaro.discord.discordControllerSendMessage(adminCommandDiscord, {
                embed: embed
            });
            await takaro.variable.variableControllerCreate({
                key: varKey,
                value: createRes.data.data.id,
                moduleId: moduleId
            });
        }
    }

    // Clean up old message ID variables if we now have fewer messages
    const allOldVariables = await takaro.variable.variableControllerSearch({
        filters: {
            moduleId: [moduleId]
        },
        search: {
            key: ['discord_admin_commands_msg_']
        }
    });

    if (allOldVariables.data.data) {
        for (const variable of allOldVariables.data.data) {
            const match = variable.key.match(/^discord_admin_commands_msg_(\d+)$/);
            if (match) {
                const index = parseInt(match[1]);
                if (index >= embeds.length) {
                    // Delete this variable as it's no longer needed
                    await takaro.variable.variableControllerDelete(variable.id);
                }
            }
        }
    }
}

await main();

