import { takaro, data, checkPermission, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { player, gameServerId, module: mod, pog } = data;

    if (!checkPermission(pog, 'STAFF_COMMANDS')) {
        throw new TakaroUserError('You do not have permission to use [02FEDC]Moderator[-] commmands.');
    }

    const itemList = mod.userConfig.droneReplacement;

    if (!itemList || !Array.isArray(itemList) || itemList.length === 0) {
        await player.pm('The drone replacement kit is empty. Please contact an administrator.');
        return;
    }

    await player.pm('Replacing your drone inventory...');

    // Loop through the list of items to give
    for (const itemEntry of itemList) {
        if (!itemEntry || !itemEntry.item) {
            console.log('Skipping invalid item entry in droneReplacement list.');
            continue;
        }

        try {
            // Find the item details using the item ID from the config
            const itemDetails = (await takaro.item.itemControllerFindOne(itemEntry.item)).data.data;
            const itemName = itemDetails.name;
            const itemCode = itemDetails.id;
            const amount = itemEntry.amount || 1;
            const quality = itemEntry.quality || '';

            // Give the item to the player
            await takaro.gameserver.gameServerControllerGiveItem(gameServerId, player.id, {
                name: itemCode,
                amount: amount,
                quality: quality,
            });

            // Notify the player
            await player.pm(`You received: ${amount}x ${itemName}`);

        } catch (error) {
            console.error(`Failed to give item with ID ${itemEntry.item}:`, error);
            await player.pm(`There was an error giving you an item (${itemEntry.item}). Please notify an admin.`);
        }
    }

    await player.pm('Drone inventory replacement complete!');
}

await main();

