import { takaro, data, checkPermission, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { player, module: mod, gameServerId, pog } = data;

    // Check Permissions
    if (!checkPermission(pog, 'CM_DM')) {
        throw new TakaroUserError('You do not have permission to use [02FEDC]Admin[-] commmands.');
    }

    await player.pm(`[EA899A]Admin Mode[-] [ADDFB3]TOGGLED[-]`);

    // Execute dm mode
    await takaro.gameserver.gameServerControllerExecuteCommand(data.gameServerId, {
        command: `eoc EOS_${pog.gameId} "dm"`,
    });

    // Execute the command CM
    await takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
        command: `eoc EOS_${pog.gameId} "cm"`,
    });

    // --- Variable Logic ---

    // Search for the adminMode variable
    const adminModeVariable = (
        await takaro.variable.variableControllerSearch({
            filters: {
                key: ['adminMode'],
                moduleId: [mod.moduleId],
                playerId: [player.id],
                gameServerId: [gameServerId],
            },
        })
    ).data.data[0];

    if (adminModeVariable) {
        // The variable already exists, increment its value
        const currentValue = parseInt(adminModeVariable.value, 10);
        const newValue = currentValue + 1;

        // Check if the new value has reached the limit of 5
        if (newValue >= 5) {
            // Define the role to be removed
            const roleId = `392e74ed-5cb8-4f9e-80a3-809b1096a5fa`;
            const playerIDs = data.player.id
            // Remove the role from the player
            await takaro.player.playerControllerRemoveRole(playerIDs, roleId);
            // Delete the variable to reset the count
            await takaro.variable.variableControllerDelete(adminModeVariable.id);

            // ** Inform the player with the new message **
            await player.pm('[FF6347]Your CM role has been removed. Please DM Mad to resolve the issue.[-]');

        } else {
            // If the limit is not reached, just update the count
            await takaro.variable.variableControllerUpdate(adminModeVariable.id, {
                value: newValue.toString(),
            });
        }
    } else {
        // The variable doesn't exist, so create it for the first time
        const expiresAt = new Date();
        expiresAt.setHours(expiresAt.getHours() + 24); // Set expiration for 24 hours from now

        await takaro.variable.variableControllerCreate({
            key: 'adminMode',
            value: '1', // Start the count at 1
            playerId: player.id,
            moduleId: mod.moduleId,
            gameServerId: gameServerId,
            expiresAt: expiresAt.toISOString(), // Add the expiration date
        });
    }
}

await main();

