import { takaro, data, TakaroUserError } from '@takaro/helpers';

function getDistance(point1, point2) {
    const dx = point1.x - point2.x;
    const dy = point1.y - point2.y;
    const dz = point1.z - point2.z;
    return Math.sqrt(dx * dx + dy * dy + dz * dz);
}

async function main() {
    const { player, gameServerId, pog, module: mod } = data;

    const playerPosition = {
        x: pog.positionX,
        y: pog.positionY,
        z: pog.positionZ,
    };

    const CONFIRMATION_VAR_KEY = `removeLCB_closest_claim_${player.id}`;

    const existingConfirmation = (await takaro.variable.variableControllerSearch({
        filters: {
            key: [CONFIRMATION_VAR_KEY],
            gameServerId: [gameServerId],
            playerId: [player.id],
            moduleId: [mod.moduleId]
        }
    })).data.data[0];

    if (existingConfirmation) {
        const storedData = JSON.parse(existingConfirmation.value);
        const expiryTime = new Date(storedData.expiry);
        const now = new Date();

        if (now < expiryTime) {
            await takaro.variable.variableControllerDelete(existingConfirmation.id);

            const closestX = storedData.x;
            const closestY = storedData.y;
            const closestZ = storedData.z;
            const ownerName = storedData.ownerName;
            const coords = `${closestX}, ${closestY}, ${closestZ}`;

            const commandToExecute = `rlp2 ${closestX} ${closestY} ${closestZ}`;
            await takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
                command: commandToExecute,
            });

            await player.pm(`Confirmed! Executed command '${commandToExecute}' for the closest land claim owned by [07F6FF]${ownerName}[-] at [70FE02]${coords}[-].`);
        } else {
            await takaro.variable.variableControllerDelete(existingConfirmation.id);
            await player.pm('Your previous /removeLCB request has expired. Please run /removeLCB again to get new information and confirm.');
        }
        return;
    }

    const landClaimsResponse = (await takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
        command: 'llp',
    })).data.data;

    const landClaimsText = landClaimsResponse.rawResult;

    const claims = [];
    const lines = landClaimsText.split(/[\r\n]+/);
    let currentOwner = null;

    const playerRegex = /^Player "([^"]+)"/;
    const coordRegex = /^\s*\((-?\d+),\s*(-?\d+),\s*(-?\d+)\)/;

    for (const line of lines) {
        const playerMatch = line.match(playerRegex);
        if (playerMatch) {
            currentOwner = playerMatch[1];
            continue;
        }

        const coordMatch = line.match(coordRegex);
        if (coordMatch && currentOwner) {
            claims.push({
                owner: currentOwner,
                position: {
                    x: parseInt(coordMatch[1], 10),
                    y: parseInt(coordMatch[2], 10),
                    z: parseInt(coordMatch[3], 10),
                },
            });
        }
    }

    let closestClaim = null;
    let minDistance = Infinity;

    for (const claim of claims) {
        const distance = getDistance(playerPosition, claim.position);

        if (distance < minDistance) {
            minDistance = distance;
            closestClaim = claim;
        }
    }

    const ownerName = closestClaim.owner.split(' (EOS_')[0];
    const coords = `${closestClaim.position.x}, ${closestClaim.position.y}, ${closestClaim.position.z}`;
    const distance = Math.round(minDistance);

    const message = `The closest land claim is owned by [07F6FF]${ownerName}[-] at [70FE02]${coords}[-]. It is approximately ${distance} blocks away. Run [FEF902]/removeLCB[-] again within 120 seconds to to remove the land claim.`;

    await player.pm(message);

    const now = new Date();
    const expiry = new Date(now.getTime() + 120 * 1000);

    await takaro.variable.variableControllerCreate({
        key: CONFIRMATION_VAR_KEY,
        value: JSON.stringify({
            x: closestClaim.position.x,
            y: closestClaim.position.y,
            z: closestClaim.position.z,
            ownerName: ownerName,
            expiry: expiry.toISOString()
        }),
        gameServerId,
        moduleId: mod.moduleId,
        playerId: player.id
    });
}

await main();

