import { takaro, data, checkPermission, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { player, pog, arguments: args } = data;

    //Check Permissions
    if (!checkPermission(pog, 'STAFF_COMMANDS')) {
        throw new TakaroUserError('You do not have permission to use [02FEDC]Administration[-] commmands. Staff has been notified and will be coming for you.');
    }

    // Use the ID of the current module directly.
    const modId = data.module.module.id;

    const res = await takaro.module.moduleControllerGetOne(modId);
    const commands = res.data.data.latestVersion.commands;

    if (args.commandName === 'all') {
        // Sort the commands alphabetically by trigger.
        commands.sort((a, b) => a.trigger.localeCompare(b.trigger));

        // Filter out duplicate commands based on the trigger name.
        const seen = new Set();
        const uniqueCommands = commands.filter(command => {
            const duplicate = seen.has(command.trigger);
            seen.add(command.trigger);
            return !duplicate;
        });

        for (const command of uniqueCommands) {
            await player.pm(`[E9EC6B]${command.trigger}[-] ${command.helpText}`);
        }
    } else {
        const foundCommand = commands.find(cmd => cmd.trigger === args.commandName);
        await player.pm(`[E9EC6B]${foundCommand.trigger}[-] ${foundCommand.helpText}`);
    }
}

