import { takaro, data, checkPermission } from '@takaro/helpers';

async function main() {
    const { module: mod, gameServerId } = data;
    const {
        bannedItems = [],
        banDuration,
        kickMessage,
        banMessage,
        warningMessage,
        arrestMessage,
        cheaterDetected,
        discordKickMessage,
        discordBanMessage,
        discordArrestMessage,
        discordWarningMessage
    } = mod.userConfig;

    if (!bannedItems || bannedItems.length === 0) {
        console.log("No banned items configured. Exiting.");
        return;
    }

    // Build lookup maps for banned items with their configs
    const itemIds = bannedItems.map(item => item.item);
    const itemResponse = await takaro.item.itemControllerSearch({
        filters: {
            id: itemIds,
            gameserverId: [gameServerId]
        }
    });

    // Map item code/name to the full banned item config (including per-item settings)
    const bannedItemConfigByCode = new Map();
    const bannedItemConfigByName = new Map();

    itemResponse.data.data.forEach(item => {
        // Find the config for this item
        const config = bannedItems.find(bi => bi.item === item.id);
        if (config) {
            bannedItemConfigByCode.set(item.code, config);
            bannedItemConfigByName.set(item.name, config);
        }
    });

    console.log(`Checking for ${bannedItemConfigByCode.size} banned items...`);

    // Get players who are online AND have been seen in the last 5 minutes for efficiency
    const fiveMinutesAgo = new Date(Date.now() - 5 * 60 * 1000).toISOString();

    const playersResponse = await takaro.playerOnGameserver.playerOnGameServerControllerSearch({
        filters: {
            gameServerId: [gameServerId],
            online: [true]
        },
        greaterThan: {
            lastSeen: fiveMinutesAgo
        }
    });

    console.log(`Found ${playersResponse.data.data.length} active players (online + seen in last 5 minutes).`);

    // Process each player
    for (const pog of playersResponse.data.data) {
        // Check if player has inventory
        if (!pog.inventory || pog.inventory.length === 0) {
            continue;
        }

        // Check for banned items - find first banned item in inventory
        const bannedItemInInventory = pog.inventory.find(item =>
            item && (bannedItemConfigByCode.has(item.code) || bannedItemConfigByName.has(item.name))
        );

        if (!bannedItemInInventory) continue;

        // Get the config for this specific banned item
        const bannedItemConfig = bannedItemConfigByCode.get(bannedItemInInventory.code) || 
                                 bannedItemConfigByName.get(bannedItemInInventory.name);

        // Get per-item settings (default to 0 warnings)
        const warningsBeforePunishment = bannedItemConfig.warningsBeforePunishment ?? 0;
        const punishmentType = bannedItemConfig.punishmentType || 'kick';

        // Check immunity for THIS SPECIFIC ITEM
        let hasImmunity = false;
        if (bannedItemConfig.immunityPermission && bannedItemConfig.immunityPermission !== 'None') {
            const fullPermission = `banned_items_immunity_${bannedItemConfig.immunityPermission.toLowerCase()}`;
            if (checkPermission(pog, fullPermission)) {
                hasImmunity = true;
                console.log(`Player ${pog.playerId} has immunity for ${bannedItemInInventory.name} via permission: ${fullPermission}`);
            }
        }

        if (hasImmunity) continue;

        // Get player name for messages
        const playerDetails = await takaro.player.playerControllerGetOne(pog.playerId);
        const playerName = playerDetails.data.data.name;

        console.log(`Player ${playerName} found with banned item: ${bannedItemInInventory.name}`);

        // Get current warnings for THIS SPECIFIC ITEM
        const warningKey = `banned_items_warning_${bannedItemConfig.item}`;
        const existingVariable = await takaro.variable.variableControllerSearch({
            filters: {
                playerId: [pog.playerId],
                key: [warningKey],
                moduleId: [data.module.moduleId]
            },
        });

        let currentWarnings = 0;
        if (existingVariable.data.data.length > 0) {
            currentWarnings = parseInt(existingVariable.data.data[0].value, 10) || 0;
        }
        currentWarnings++;

        // Apply punishment if warnings exceeded
        if (currentWarnings > warningsBeforePunishment && punishmentType !== 'none') {
            const itemMessage = bannedItemInInventory.name;

            if (punishmentType === 'arrest') {
                const formattedMessage = arrestMessage
                    .replace('{pname}', playerName)
                    .replace('{item}', itemMessage);
                const formattedMessageDiscord = discordArrestMessage
                    .replace('{pname}', playerName)
                    .replace('{item}', itemMessage);

                await takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
                    command: `arrest "EOS_${playerDetails.data.data.epicOnlineServicesId}"`,
                });

                if (cheaterDetected && discordArrestMessage) {
                    await takaro.discord.discordControllerSendMessage(cheaterDetected, {
                        message: formattedMessageDiscord,
                    });
                }

                await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                    message: formattedMessage,
                });
            } else if (punishmentType === 'kick') {
                const formattedMessageDiscord = discordKickMessage
                    .replace('{pname}', playerName)
                    .replace('{item}', itemMessage);

                await takaro.gameserver.gameServerControllerKickPlayer(gameServerId, pog.playerId, {
                    reason: kickMessage.replace('{item}', itemMessage)
                });

                await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                    message: `${playerName} has been kicked for having banned item: ${itemMessage}`
                });

                if (cheaterDetected && discordKickMessage) {
                    await takaro.discord.discordControllerSendMessage(cheaterDetected, {
                        message: formattedMessageDiscord,
                    });
                }
            } else if (punishmentType === 'ban') {
                const formattedMessageDiscord = discordBanMessage
                    .replace('{pname}', playerName)
                    .replace('{item}', itemMessage);

                const now = new Date();
                const expiresAt = new Date(now.getTime() + banDuration);
                await takaro.player.banControllerCreate({
                    gameServerId,
                    playerId: pog.playerId,
                    until: expiresAt,
                    reason: banMessage.replace('{item}', itemMessage)
                });

                await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                    message: `${playerName} has been banned for having banned item: ${itemMessage}`
                });

                if (cheaterDetected && discordBanMessage) {
                    await takaro.discord.discordControllerSendMessage(cheaterDetected, {
                        message: formattedMessageDiscord,
                    });
                }
            }

            // Reset warnings after punishment
            if (existingVariable.data.data.length > 0) {
                await takaro.variable.variableControllerDelete(existingVariable.data.data[0].id);
            }
        } else {
            // Issue warning
            let message = warningMessage.replace('{item}', bannedItemInInventory.name);
            if (punishmentType !== 'none' && warningsBeforePunishment > 0) {
                message += ` Warning ${currentWarnings} of ${warningsBeforePunishment} before ${punishmentType}`;
            }

            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, { message });

            // Send Discord warning notification if configured
            if (cheaterDetected && discordWarningMessage) {
                const formattedWarningDiscord = discordWarningMessage
                    .replace('{pname}', playerName)
                    .replace('{item}', bannedItemInInventory.name)
                    .replace('{warnings}', currentWarnings.toString())
                    .replace('{maxWarnings}', warningsBeforePunishment.toString());

                await takaro.discord.discordControllerSendMessage(cheaterDetected, {
                    message: formattedWarningDiscord,
                });
            }

            // Update warning count for this specific item
            if (existingVariable.data.data.length > 0) {
                await takaro.variable.variableControllerUpdate(existingVariable.data.data[0].id, {
                    value: currentWarnings.toString()
                });
            } else {
                await takaro.variable.variableControllerCreate({
                    playerId: pog.playerId,
                    key: warningKey,
                    value: currentWarnings.toString(),
                    moduleId: data.module.moduleId
                });
            }
        }
    }

    console.log("Finished checking all online players.");
}

await main();

