import { data, takaro } from '@takaro/helpers';

async function main() {
    const { gameServerId, player, pog, module } = data;

    try {
        if (!player) {
            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                message: 'Error: Player information not available',
                opts: { recipient: { gameId: pog.gameId } }
            });
            return;
        }

        const config = module.userConfig;
        const levels = config.levels || [];
        const messages = config.messages || {};

        // Get player variables
        const variablesSearch = await takaro.variable.variableControllerSearch({
            filters: {
                gameServerId: [gameServerId],
                playerId: [player.id],
                moduleId: [module.moduleId]
            }
        });

        let playerXP = 0;
        let playerLevel = 0;
        let totalPlaytimeMinutes = 0;
        let killStats = {};

        // Parse variables
        for (const variable of variablesSearch.data.data) {
            switch (variable.key) {
                case 'playerXP':
                    playerXP = parseInt(variable.value) || 0;
                    break;
                case 'playerLevel':
                    playerLevel = parseInt(variable.value) || 0;
                    break;
                case 'totalPlaytimeMinutes':
                    totalPlaytimeMinutes = parseInt(variable.value) || 0;
                    break;
                default:
                    if (variable.key.startsWith('totalKills_')) {
                        const entityName = variable.key.replace('totalKills_', '');
                        killStats[entityName] = parseInt(variable.value) || 0;
                    }
                    break;
            }
        }

        // Calculate current level inline (same logic as checkLevelUp function)
        const sortedLevels = levels.sort((a, b) => a.xpRequired - b.xpRequired);

        let currentLevel = 0;
        let currentLevelData = null;
        let nextLevelData = null;

        for (let i = 0; i < sortedLevels.length; i++) {
            if (playerXP >= sortedLevels[i].xpRequired) {
                currentLevel = sortedLevels[i].level;
                currentLevelData = sortedLevels[i];
            } else {
                nextLevelData = sortedLevels[i];
                break;
            }
        }

        const xpToNextLevel = nextLevelData ? nextLevelData.xpRequired - playerXP : 0;

        // Format playtime
        const hours = Math.floor(totalPlaytimeMinutes / 60);
        const minutes = totalPlaytimeMinutes % 60;
        const playtimeText = hours > 0 ? `${hours}h ${minutes}m` : `${minutes}m`;

        // Build progression message
        let progressMessage = `=== ${player.name}'s Progression ===\\n`;
        progressMessage += `Level: ${currentLevel}`;

        if (currentLevelData && currentLevelData.name) {
            progressMessage += ` - ${currentLevelData.name}`;
        }

        progressMessage += `\\nXP: ${playerXP}`;

        if (nextLevelData) {
            progressMessage += ` / ${nextLevelData.xpRequired}`;
            progressMessage += `\\nNext Level: ${xpToNextLevel} XP needed`;
            if (nextLevelData.name) {
                progressMessage += ` (${nextLevelData.name})`;
            }
        } else {
            progressMessage += `\\nðŸ† Max level reached!`;
        }

        progressMessage += `\\nPlaytime: ${playtimeText}`;

        // Add kill stats
        if (Object.keys(killStats).length > 0) {
            progressMessage += `\\n\\nKills:`;
            const sortedKills = Object.entries(killStats)
                .sort(([,a], [,b]) => b - a)
                .slice(0, 5); // Top 5

            for (const [entity, count] of sortedKills) {
                progressMessage += `\\n  ${entity}: ${count}`;
            }
        }

        // Show next level rewards
        if (nextLevelData) {
            progressMessage += `\\n\\nNext Level Rewards:`;
            if (nextLevelData.currencyReward > 0) {
                progressMessage += `\\n  ðŸ’° ${nextLevelData.currencyReward} currency`;
            }
            if (nextLevelData.itemRewards && nextLevelData.itemRewards.length > 0) {
                for (const item of nextLevelData.itemRewards) {
                    progressMessage += `\\n  ðŸ“¦ ${item.amount}x ${item.itemCode}`;
                }
            }
        }

        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: progressMessage,
            opts: { recipient: { gameId: player.gameId } }
        });

        console.log(`Progression stats shown to ${player.name}: Level ${currentLevel}, XP ${playerXP}`);

    } catch (error) {
        console.error('Error in progression command:', error);
        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: 'Error retrieving progression data. Please try again.',
            opts: { recipient: { gameId: pog.gameId } }
        });
    }
}

await main();

import { data, takaro } from '@takaro/helpers';

async function main() {
    const { gameServerId, player, pog, module } = data;

    try {
        if (!player) {
            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                message: 'Error: Player information not available',
                opts: { recipient: { gameId: pog.gameId } }
            });
            return;
        }

        const config = module.userConfig;
        const levels = config.levels || [];
        const messages = config.messages || {};

        // Get player XP
        const xpVarSearch = await takaro.variable.variableControllerSearch({
            filters: {
                gameServerId: [gameServerId],
                playerId: [player.id],
                moduleId: [module.moduleId],
                key: ['playerXP']
            }
        });

        let playerXP = 0;
        if (xpVarSearch.data.data.length > 0) {
            playerXP = parseInt(xpVarSearch.data.data[0].value) || 0;
        }

        // Get level info
        const levelResult = await takaro.function.functionControllerTrigger('6256101e-831c-4a82-b93b-54978ed1f29e', {
            playerId: player.id,
            gameServerId,
            currentXP: playerXP,
            module
        });

        const currentLevel = levelResult.data.data.currentLevel;

        if (currentLevel === 0) {
            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                message: 'You haven\'t reached any levels yet! Keep playing to gain XP.',
                opts: { recipient: { gameId: player.gameId } }
            });
            return;
        }

        // Get all claimed rewards
        const claimedVarsSearch = await takaro.variable.variableControllerSearch({
            filters: {
                gameServerId: [gameServerId],
                playerId: [player.id],
                moduleId: [module.moduleId]
            }
        });

        const claimedLevels = new Set();
        for (const variable of claimedVarsSearch.data.data) {
            if (variable.key.startsWith('claimedRewards_')) {
                const level = parseInt(variable.key.replace('claimedRewards_', ''));
                claimedLevels.add(level);
            }
        }

        // Sort levels by level number
        const sortedLevels = levels.sort((a, b) => a.level - b.level);

        // Find unclaimed levels
        const unclaimedLevels = [];
        for (const levelData of sortedLevels) {
            if (levelData.level <= currentLevel && !claimedLevels.has(levelData.level)) {
                unclaimedLevels.push(levelData);
            }
        }

        if (unclaimedLevels.length === 0) {
            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                message: 'No unclaimed rewards available. All your level rewards have been claimed!',
                opts: { recipient: { gameId: player.gameId } }
            });
            return;
        }

        // Claim all unclaimed rewards
        let totalCurrency = 0;
        let allItems = [];

        for (const levelData of unclaimedLevels) {
            try {
                // Grant rewards
                const rewardResult = await takaro.function.functionControllerTrigger('5fcae3b7-f95c-4e43-bdd9-ccb32d570d24', {
                    playerId: player.id,
                    gameServerId,
                    levelData,
                    module,
                    playerName: player.name
                });

                // Track rewards for summary
                if (levelData.currencyReward > 0) {
                    totalCurrency += levelData.currencyReward;
                }

                if (levelData.itemRewards && levelData.itemRewards.length > 0) {
                    allItems.push(...levelData.itemRewards);
                }

                console.log(`Claimed level ${levelData.level} rewards for ${player.name}`);

            } catch (rewardError) {
                console.error(`Error claiming level ${levelData.level} rewards:`, rewardError);
            }
        }

        // Send summary message
        let summaryMessage = `âœ… Claimed rewards for ${unclaimedLevels.length} levels!`;

        if (totalCurrency > 0) {
            summaryMessage += `\nðŸ’° Total currency: ${totalCurrency}`;
        }

        if (allItems.length > 0) {
            const itemSummary = {};
            for (const item of allItems) {
                const key = `${item.itemCode}${item.quality ? ` (${item.quality})` : ''}`;
                itemSummary[key] = (itemSummary[key] || 0) + item.amount;
            }

            summaryMessage += `\nðŸ“¦ Items received:`;
            for (const [itemDesc, count] of Object.entries(itemSummary)) {
                summaryMessage += `\n  ${count}x ${itemDesc}`;
            }
        }

        const levelsText = unclaimedLevels.map(l => l.level).join(', ');
        summaryMessage += `\n\nLevels claimed: ${levelsText}`;

        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: summaryMessage,
            opts: { recipient: { gameId: player.gameId } }
        });

        console.log(`${player.name} claimed rewards for levels: ${levelsText}`);

    } catch (error) {
        console.error('Error in claim command:', error);
        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: 'Error claiming rewards. Please try again.',
            opts: { recipient: { gameId: pog.gameId } }
        });
    }
}

await main();

import { data, takaro } from '@takaro/helpers';

async function main() {
    const { gameServerId, player, pog, module, arguments: args } = data;

    try {
        if (!player) {
            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                message: 'Error: Player information not available',
                opts: { recipient: { gameId: pog.gameId } }
            });
            return;
        }

        const targetPlayer = args.target || '';

        if (targetPlayer.toLowerCase() === 'all') {
            // Reset all players on this server
            await resetAllPlayers(gameServerId, module.moduleId, player.name);
        } else if (targetPlayer.trim() !== '') {
            // Reset specific player
            await resetSpecificPlayer(gameServerId, module.moduleId, targetPlayer, player.name);
        } else {
            // No argument provided, show help
            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                message: 'Usage: @resetprogression <playerName> or @resetprogression all\\nResets progression data for specified player or all players.',
                opts: { recipient: { gameId: player.gameId } }
            });
            return;
        }

    } catch (error) {
        console.error('Error in reset progression command:', error);
        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: 'Error resetting progression data. Please try again.',
            opts: { recipient: { gameId: pog.gameId } }
        });
    }
}

async function resetAllPlayers(gameServerId, moduleId, adminName) {
    try {
        // Find all variables for this module on this server
        const allVariables = await takaro.variable.variableControllerSearch({
            filters: {
                gameServerId: [gameServerId],
                moduleId: [moduleId]
            },
            limit: 1000 // Get many variables
        });

        const progressionKeys = ['playerXP', 'playerLevel', 'totalPlaytimeMinutes'];
        let deletedCount = 0;
        let playerCount = 0;
        const processedPlayers = new Set();

        // Delete progression variables for all players
        for (const variable of allVariables.data.data) {
            const isProgressionVar = progressionKeys.includes(variable.key) || 
                                   variable.key.startsWith('totalKills_') || 
                                   variable.key.startsWith('claimedRewards_');

            if (isProgressionVar) {
                await takaro.variable.variableControllerDelete(variable.id);
                deletedCount++;

                if (!processedPlayers.has(variable.playerId)) {
                    processedPlayers.add(variable.playerId);
                    playerCount++;
                }
            }
        }

        // Send confirmation message
        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: `ðŸ”„ Admin ${adminName} reset progression data for ALL PLAYERS\\nâœ… Reset ${playerCount} players (${deletedCount} variables cleared)\\nâš ï¸ All XP, levels, playtime, and kill stats have been wiped!`
        });

        console.log(`Admin ${adminName} reset progression for ${playerCount} players (${deletedCount} variables deleted)`);

    } catch (error) {
        console.error('Error resetting all players:', error);
        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: 'Error resetting all player data. Please try again.'
        });
    }
}

async function resetSpecificPlayer(gameServerId, moduleId, targetPlayerName, adminName) {
    try {
        // Find the target player by name
        const playerSearch = await takaro.player.playerControllerSearch({
            search: { name: [targetPlayerName] },
            limit: 10
        });

        if (!playerSearch.data.data || playerSearch.data.data.length === 0) {
            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                message: `âŒ Player "${targetPlayerName}" not found. Make sure the name is spelled correctly.`
            });
            return;
        }

        // Find exact match or closest match
        let targetPlayer = null;
        for (const player of playerSearch.data.data) {
            if (player.name.toLowerCase() === targetPlayerName.toLowerCase()) {
                targetPlayer = player;
                break;
            }
        }

        if (!targetPlayer) {
            // Use first result if no exact match
            targetPlayer = playerSearch.data.data[0];
        }

        // Find all variables for this specific player
        const playerVariables = await takaro.variable.variableControllerSearch({
            filters: {
                gameServerId: [gameServerId],
                playerId: [targetPlayer.id],
                moduleId: [moduleId]
            },
            limit: 100
        });

        const progressionKeys = ['playerXP', 'playerLevel', 'totalPlaytimeMinutes'];
        let deletedCount = 0;

        // Delete progression variables for this player
        for (const variable of playerVariables.data.data) {
            const isProgressionVar = progressionKeys.includes(variable.key) || 
                                   variable.key.startsWith('totalKills_') || 
                                   variable.key.startsWith('claimedRewards_');

            if (isProgressionVar) {
                await takaro.variable.variableControllerDelete(variable.id);
                deletedCount++;
            }
        }

        // Send confirmation message
        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: `ðŸ”„ Admin ${adminName} reset progression data for ${targetPlayer.name}\\nâœ… Cleared ${deletedCount} progression variables\\nâš ï¸ ${targetPlayer.name}'s XP, level, playtime, and kill stats have been reset!`
        });

        console.log(`Admin ${adminName} reset progression for ${targetPlayer.name} (${deletedCount} variables deleted)`);

    } catch (error) {
        console.error('Error resetting specific player:', error);
        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: `Error resetting data for "${targetPlayerName}". Please try again.`
        });
    }
}

await main();

