import { data, takaro } from '@takaro/helpers';

async function main() {
    const { gameServerId, module } = data;

    try {
        const config = module.userConfig;
        const xpSettings = config.xpSettings || {};
        const xpPerHour = xpSettings.xpPerHourPlayed || 100;
        const checkIntervalMinutes = xpSettings.activePlayerMinutes || 5;

        // Get all online players on this server
        const playersResult = await takaro.gameserver.gameServerControllerGetPlayers(gameServerId);

        if (!playersResult.data.data || playersResult.data.data.length === 0) {
            console.log('No online players found');
            return;
        }

        console.log(`Checking active XP for ${playersResult.data.data.length} online players`);

        for (const gamePlayer of playersResult.data.data) {
            try {
                const playerName = gamePlayer.name;

                // Find Takaro player ID by searching for players with matching identifiers
                let playerSearchResult;

                if (gamePlayer.steamId) {
                    playerSearchResult = await takaro.player.playerControllerSearch({
                        filters: {
                            steamId: [gamePlayer.steamId]
                        },
                        limit: 1
                    });
                } else if (gamePlayer.epicOnlineServicesId) {
                    playerSearchResult = await takaro.player.playerControllerSearch({
                        filters: {
                            epicOnlineServicesId: [gamePlayer.epicOnlineServicesId]
                        },
                        limit: 1
                    });
                }

                if (!playerSearchResult || !playerSearchResult.data.data || playerSearchResult.data.data.length === 0) {
                    console.log(`Could not find Takaro player for game player: ${playerName}`);
                    continue;
                }

                const playerId = playerSearchResult.data.data[0].id;

                // Calculate XP for the interval
                const baseXPForInterval = Math.floor((xpPerHour / 60) * checkIntervalMinutes);

                if (baseXPForInterval <= 0) {
                    continue;
                }

                // Inline XP multiplier calculation (from calculateXPWithMultiplier function)
                let multiplier = 1;
                try {
                    const playerInfo = await takaro.playerOnGameserver.playerOnGameServerControllerGetOne(gameServerId, playerId);

                    if (playerInfo.data.data.roles && playerInfo.data.data.roles.length > 0) {
                        for (const roleAssignment of playerInfo.data.data.roles) {
                            if (roleAssignment.role && roleAssignment.role.permissions) {
                                for (const perm of roleAssignment.role.permissions) {
                                    if (perm.permission && perm.permission.permission === 'XP_MULTIPLIER' && perm.permission.moduleVersionId === module.versionId) {
                                        const count = perm.count || 0;
                                        if (count > multiplier) {
                                            multiplier = count;
                                        }
                                    }
                                }
                            }
                        }
                    }
                } catch (permError) {
                    console.log(`Could not check permissions for ${playerName}, using 1x multiplier`);
                }

                const finalXP = Math.floor(baseXPForInterval * multiplier);

                // Get current player XP
                const xpVarSearch = await takaro.variable.variableControllerSearch({
                    filters: {
                        gameServerId: [gameServerId],
                        playerId: [playerId],
                        moduleId: [module.moduleId],
                        key: ['playerXP']
                    }
                });

                let currentXP = 0;
                if (xpVarSearch.data.data.length > 0) {
                    currentXP = parseInt(xpVarSearch.data.data[0].value) || 0;
                }

                const newXP = currentXP + finalXP;

                // Update XP variable
                if (xpVarSearch.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(xpVarSearch.data.data[0].id, {
                        value: newXP.toString()
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: 'playerXP',
                        value: newXP.toString(),
                        gameServerId,
                        playerId,
                        moduleId: module.moduleId
                    });
                }

                // Update total playtime tracking
                const playtimeVarSearch = await takaro.variable.variableControllerSearch({
                    filters: {
                        gameServerId: [gameServerId],
                        playerId: [playerId],
                        moduleId: [module.moduleId],
                        key: ['totalPlaytimeMinutes']
                    }
                });

                let totalMinutes = 0;
                if (playtimeVarSearch.data.data.length > 0) {
                    totalMinutes = parseInt(playtimeVarSearch.data.data[0].value) || 0;
                }

                const newTotalMinutes = totalMinutes + checkIntervalMinutes;

                if (playtimeVarSearch.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(playtimeVarSearch.data.data[0].id, {
                        value: newTotalMinutes.toString()
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: 'totalPlaytimeMinutes',
                        value: checkIntervalMinutes.toString(),
                        gameServerId,
                        playerId,
                        moduleId: module.moduleId
                    });
                }

                // Inline level checking (from checkLevelUp function)
                const levels = config.levels || [];
                const sortedLevels = levels.sort((a, b) => a.xpRequired - b.xpRequired);

                let currentLevel = 0;
                let currentLevelData = null;
                let nextLevelData = null;

                for (let i = 0; i < sortedLevels.length; i++) {
                    if (newXP >= sortedLevels[i].xpRequired) {
                        currentLevel = sortedLevels[i].level;
                        currentLevelData = sortedLevels[i];
                    } else {
                        nextLevelData = sortedLevels[i];
                        break;
                    }
                }

                // Get stored level from variables
                const levelVarSearch = await takaro.variable.variableControllerSearch({
                    filters: {
                        gameServerId: [gameServerId],
                        playerId: [playerId],
                        moduleId: [module.moduleId],
                        key: ['playerLevel']
                    }
                });

                const storedLevel = levelVarSearch.data.data.length > 0 
                    ? parseInt(levelVarSearch.data.data[0].value) 
                    : 0;

                const leveledUp = currentLevel > storedLevel;

                // Update stored level if changed
                if (leveledUp) {
                    if (levelVarSearch.data.data.length > 0) {
                        await takaro.variable.variableControllerUpdate(levelVarSearch.data.data[0].id, {
                            value: currentLevel.toString()
                        });
                    } else {
                        await takaro.variable.variableControllerCreate({
                            key: 'playerLevel',
                            value: currentLevel.toString(),
                            gameServerId,
                            playerId,
                            moduleId: module.moduleId
                        });
                    }

                    // Inline reward granting (from grantRewards function)
                    if (currentLevelData) {
                        const messages = config.messages || {};
                        let rewardsGranted = [];

                        // Grant currency reward
                        if (currentLevelData.currencyReward && currentLevelData.currencyReward > 0) {
                            await takaro.playerOnGameserver.playerOnGameServerControllerAddCurrency(
                                gameServerId, 
                                playerId, 
                                { currency: currentLevelData.currencyReward }
                            );
                            rewardsGranted.push(`${currentLevelData.currencyReward} currency`);
                        }

                        // Grant item rewards
                        if (currentLevelData.itemRewards && currentLevelData.itemRewards.length > 0) {
                            for (const itemReward of currentLevelData.itemRewards) {
                                try {
                                    await takaro.gameserver.gameServerControllerGiveItem(gameServerId, playerId, {
                                        name: itemReward.itemCode,
                                        amount: itemReward.amount,
                                        quality: itemReward.quality || ''
                                    });
                                    rewardsGranted.push(`${itemReward.amount}x ${itemReward.itemCode}`);
                                } catch (itemError) {
                                    console.error(`Failed to grant item ${itemReward.itemCode}:`, itemError);
                                }
                            }
                        }

                        // Mark rewards as claimed
                        const claimKey = `claimedRewards_${currentLevelData.level}`;
                        await takaro.variable.variableControllerCreate({
                            key: claimKey,
                            value: new Date().toISOString(),
                            gameServerId,
                            playerId,
                            moduleId: module.moduleId
                        });

                        // Send level up message
                        const levelUpMessage = messages.levelUpMessage || 'ðŸŽ‰ {playerName} reached Level {level} - {levelName}!';
                        const formattedMessage = levelUpMessage
                            .replace('{playerName}', playerName || 'Player')
                            .replace('{level}', currentLevelData.level.toString())
                            .replace('{levelName}', currentLevelData.name || `Level ${currentLevelData.level}`);

                        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                            message: formattedMessage
                        });

                        if (rewardsGranted.length > 0) {
                            const rewardsMessage = `Rewards: ${rewardsGranted.join(', ')}`;
                            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                                message: rewardsMessage
                            });
                        }

                        console.log(`Level up! ${playerName} reached level ${currentLevel}, rewards: ${rewardsGranted.join(', ')}`);
                    }
                }

                console.log(`Playtime XP: ${playerName} gained ${finalXP} XP (${multiplier}x multiplier). Total XP: ${newXP}, Level: ${currentLevel}`);

            } catch (playerError) {
                console.error(`Error processing player ${gamePlayer.name}:`, playerError);
            }
        }

        console.log('Active player XP processing completed');

    } catch (error) {
        console.error('Error in active player XP cronjob:', error);
    }
}

await main();

