import { data, takaro } from '@takaro/helpers';

async function main() {
    const { gameServerId, module } = data;

    try {
        const config = module.userConfig;
        const xpSettings = config.xpSettings || {};
        const xpPerHour = xpSettings.xpPerHourPlayed || 100;
        const checkIntervalMinutes = xpSettings.activePlayerMinutes || 5;

        // Get all online players on this server
        const playersResult = await takaro.gameserver.gameServerControllerGetPlayers(gameServerId);

        if (!playersResult.data.data || playersResult.data.data.length === 0) {
            console.log('No online players found');
            return;
        }

        console.log(`Checking active XP for ${playersResult.data.data.length} online players`);

        for (const gamePlayer of playersResult.data.data) {
            try {
                const playerName = gamePlayer.name;

                // Find Takaro player ID by searching for players with matching identifiers
                let playerSearchResult;

                if (gamePlayer.steamId) {
                    playerSearchResult = await takaro.player.playerControllerSearch({
                        filters: {
                            steamId: [gamePlayer.steamId]
                        },
                        limit: 1
                    });
                } else if (gamePlayer.epicOnlineServicesId) {
                    playerSearchResult = await takaro.player.playerControllerSearch({
                        filters: {
                            epicOnlineServicesId: [gamePlayer.epicOnlineServicesId]
                        },
                        limit: 1
                    });
                }

                if (!playerSearchResult || !playerSearchResult.data.data || playerSearchResult.data.data.length === 0) {
                    console.log(`Could not find Takaro player for game player: ${playerName}`);
                    continue;
                }

                const playerId = playerSearchResult.data.data[0].id;

                // Calculate XP for the interval
                const baseXPForInterval = Math.floor((xpPerHour / 60) * checkIntervalMinutes);

                if (baseXPForInterval <= 0) {
                    continue;
                }

                // Inline XP multiplier calculation (from calculateXPWithMultiplier function)
                let multiplier = 1;
                try {
                    const playerInfo = await takaro.playerOnGameserver.playerOnGameServerControllerGetOne(gameServerId, playerId);

                    if (playerInfo.data.data.roles && playerInfo.data.data.roles.length > 0) {
                        for (const roleAssignment of playerInfo.data.data.roles) {
                            if (roleAssignment.role && roleAssignment.role.permissions) {
                                for (const perm of roleAssignment.role.permissions) {
                                    if (perm.permission && perm.permission.permission === 'XP_MULTIPLIER' && perm.permission.moduleVersionId === module.versionId) {
                                        const count = perm.count || 0;
                                        if (count > multiplier) {
                                            multiplier = count;
                                        }
                                    }
                                }
                            }
                        }
                    }
                } catch (permError) {
                    console.log(`Could not check permissions for ${playerName}, using 1x multiplier`);
                }

                const finalXP = Math.floor(baseXPForInterval * multiplier);

                // Get current player XP
                const xpVarSearch = await takaro.variable.variableControllerSearch({
                    filters: {
                        gameServerId: [gameServerId],
                        playerId: [playerId],
                        moduleId: [module.moduleId],
                        key: ['playerXP']
