                    currentXP = parseInt(xpVarSearch.data.data[0].value) || 0;
                }

                const newXP = currentXP + finalXP;

                // Update XP variable
                if (xpVarSearch.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(xpVarSearch.data.data[0].id, {
                        value: newXP.toString()
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: 'playerXP',
                        value: newXP.toString(),
                        gameServerId,
                        playerId,
                        moduleId: module.moduleId
                    });
                }

                // Update total playtime tracking
                const playtimeVarSearch = await takaro.variable.variableControllerSearch({
                    filters: {
                        gameServerId: [gameServerId],
                        playerId: [playerId],
                        moduleId: [module.moduleId],
                        key: ['totalPlaytimeMinutes']
                    }
                });

                let totalMinutes = 0;
                if (playtimeVarSearch.data.data.length > 0) {
                    totalMinutes = parseInt(playtimeVarSearch.data.data[0].value) || 0;
                }

                const newTotalMinutes = totalMinutes + checkIntervalMinutes;

                if (playtimeVarSearch.data.data.length > 0) {
                    await takaro.variable.variableControllerUpdate(playtimeVarSearch.data.data[0].id, {
                        value: newTotalMinutes.toString()
                    });
                } else {
                    await takaro.variable.variableControllerCreate({
                        key: 'totalPlaytimeMinutes',
                        value: checkIntervalMinutes.toString(),
                        gameServerId,
                        playerId,
                        moduleId: module.moduleId
                    });
                }

                // Inline level checking (from checkLevelUp function)
                const levels = config.levels || [];
                const sortedLevels = levels.sort((a, b) => a.xpRequired - b.xpRequired);

                let currentLevel = 0;
                let currentLevelData = null;
                let nextLevelData = null;

                for (let i = 0; i < sortedLevels.length; i++) {
                    if (newXP >= sortedLevels[i].xpRequired) {
                        currentLevel = sortedLevels[i].level;
                        currentLevelData = sortedLevels[i];
                    } else {
                        nextLevelData = sortedLevels[i];
                        break;
                    }
