                    gameServerId,
                    playerId,
                    moduleId: module.moduleId
                });
            }
        }

        console.log(`Player level check: Current XP: ${currentXP}, Level: ${currentLevel}, Leveled up: ${leveledUp}`);

        return {
            currentLevel,
            currentLevelData,
            nextLevelData,
            previousLevel: storedLevel,
            leveledUp,
            xpToNextLevel: nextLevelData ? nextLevelData.xpRequired - currentXP : 0
        };
