        if (levelData.itemRewards && levelData.itemRewards.length > 0) {
            for (const itemReward of levelData.itemRewards) {
                try {
                    await takaro.gameserver.gameServerControllerGiveItem(gameServerId, playerId, {
                        name: itemReward.itemCode,
                        amount: itemReward.amount,
                        quality: itemReward.quality || ''
                    });
                    rewardsGranted.push(`${itemReward.amount}x ${itemReward.itemCode}`);
                    console.log(`Granted ${itemReward.amount}x ${itemReward.itemCode} to player ${playerId}`);
                } catch (itemError) {
                    console.error(`Failed to grant item ${itemReward.itemCode}:`, itemError);
                }
            }
        }

        // Mark rewards as claimed
        const claimKey = `claimedRewards_${levelData.level}`;
        await takaro.variable.variableControllerCreate({
            key: claimKey,
            value: new Date().toISOString(),
            gameServerId,
            playerId,
            moduleId: module.moduleId
        });

        // Send level up message
        const levelUpMessage = messages.levelUpMessage || 'ðŸŽ‰ {playerName} reached Level {level} - {levelName}!';
        const formattedMessage = levelUpMessage
            .replace('{playerName}', playerName || 'Player')
            .replace('{level}', levelData.level.toString())
            .replace('{levelName}', levelData.name || `Level ${levelData.level}`);

        await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
            message: formattedMessage
        });

        if (rewardsGranted.length > 0) {
            const rewardsMessage = `Rewards: ${rewardsGranted.join(', ')}`;
            await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                message: rewardsMessage
            });
        }

        console.log(`Level ${levelData.level} rewards granted to ${playerName}: ${rewardsGranted.join(', ')}`);

        return {
            success: true,
            rewardsGranted,
            message: formattedMessage
        };

    } catch (error) {
        console.error('Error granting rewards:', error);
        return {
            success: false,
            error: error.message,
            rewardsGranted: []
        };
    }
}

await main();

import { data, takaro } from '@takaro/helpers';

async function main() {
    const { playerId, gameServerId, baseXP, moduleId } = data;

    try {
        // Get player's roles and permissions
        const playerInfo = await takaro.playerOnGameserver.playerOnGameServerControllerGetOne(gameServerId, playerId);

        let multiplier = 1;

        // Check for XP_MULTIPLIER permission
        if (playerInfo.data.data.roles && playerInfo.data.data.roles.length > 0) {
            for (const roleAssignment of playerInfo.data.data.roles) {
                if (roleAssignment.role && roleAssignment.role.permissions) {
                    for (const perm of roleAssignment.role.permissions) {
                        if (perm.permission && perm.permission.permission === 'XP_MULTIPLIER' && perm.permission.moduleVersionId === moduleId) {
                            // Use the count as multiplier, default to 1 if count is 0
                            const count = perm.count || 0;
                            if (count > multiplier) {
                                multiplier = count;
                            }
                        }
                    }
                }
            }
        }

        const finalXP = Math.floor(baseXP * multiplier);

        console.log(`Base XP: ${baseXP}, Multiplier: ${multiplier}x, Final XP: ${finalXP}`);

        return {
            baseXP,
            multiplier,
            finalXP
        };
    } catch (error) {
        console.error('Error calculating XP with multiplier:', error);
        return {
            baseXP,
            multiplier: 1,
            finalXP: baseXP
        };
    }
}

await main();
