import { data, takaro, checkPermission, TakaroUserError } from '@takaro/helpers';

async function main() {
    const { gameServerId, pog, arguments: args, module, player } = data;

    console.log(`‚ö° /getbuff command executed by ${player?.name || pog.gameId}`);

    const config = module.userConfig;
    const buffPackages = config.buffPackages || [];

    if (buffPackages.length === 0) {
        console.log('‚ùå No buff packages configured');
        throw new TakaroUserError('No buff packages are configured. Please contact an administrator.');
    }

    const packageName = args.packageName?.trim() || '';

    // If no package specified, list available packages
    if (!packageName) {
        console.log('üìã Listing available buff packages');
        const availablePackages = buffPackages
            .map(pkg => {
                const cost = pkg.currencyCost > 0 ? ` (Cost: ${pkg.currencyCost})` : '';
                const role = pkg.requiredRoleId && pkg.requiredRoleId.trim() ? ' [Role Required]' : '';

                // Convert duration from milliseconds to readable format
                let durationText = '';
                if (pkg.duration > 0) {
                    const minutes = Math.floor(pkg.duration / 60000);
                    const hours = Math.floor(minutes / 60);
                    if (hours > 0) {
                        durationText = ` [Duration: ${hours}h ${minutes % 60}m]`;
                    } else {
                        durationText = ` [Duration: ${minutes}m]`;
                    }
                } else {
                    durationText = ' [Permanent]';
                }

                return `‚Ä¢ ${pkg.commandName} - ${pkg.displayName}${cost}${role}${durationText}${pkg.description ? '\n  ' + pkg.description : ''}`;
            })
            .join('\n');

        await pog.pm(`Available buff packages:\n${availablePackages}\n\nUsage: getbuff <packageName>`);
        console.log('‚úÖ Package list sent');
        return;
    }

    // Find the requested package
    const pkg = buffPackages.find(p => p.commandName.toLowerCase() === packageName.toLowerCase());

    if (!pkg) {
        console.log(`‚ùå Package "${packageName}" not found`);
        throw new TakaroUserError(`Buff package "${packageName}" not found. Use /getbuff to see available packages.`);
    }

    console.log(`üéØ Found package: ${pkg.displayName}`);

    // Check role requirement
    if (pkg.requiredRoleId && pkg.requiredRoleId.trim() !== '') {
        const playerRoles = pog.roles || [];
        const hasRole = playerRoles.some(r => r.role.id === pkg.requiredRoleId);

        console.log(`üîê Role check: ${hasRole ? '‚úì HAS' : '‚úó NO'} required role ${pkg.requiredRoleId}`);

        if (!hasRole) {
            console.log('‚ùå Command denied - insufficient role');
            throw new TakaroUserError(`You don't have the required role to use this buff package.`);
        }
    } else {
        console.log('üîê Role check: ‚úì No role required');
    }

    // Handle currency cost
    if (pkg.currencyCost > 0) {
        try {
            const pogData = await takaro.playerOnGameserver.playerOnGameServerControllerGetOne(gameServerId, player.id);
            const currentCurrency = pogData.data.data.currency || 0;

            console.log(`üí∞ Currency check: ${currentCurrency} >= ${pkg.currencyCost}`);

            if (currentCurrency < pkg.currencyCost) {
                console.log('‚ùå Insufficient currency');
                const currencyName = (await takaro.settings.settingsControllerGetOne('currencyName', gameServerId)).data.data.value;
                throw new TakaroUserError(`Insufficient ${currencyName}. Need ${pkg.currencyCost}, have ${currentCurrency}.`);
            }

            // Deduct currency using negative value
            await takaro.playerOnGameserver.playerOnGameServerControllerAddCurrency(gameServerId, player.id, {
                currency: -pkg.currencyCost
            });
            console.log(`üí∞ Deducted ${pkg.currencyCost} currency`);
        } catch (err) {
            if (err instanceof TakaroUserError) {
                throw err;
            }
            console.error('‚ùå Currency operation failed:', err.message);
            throw new TakaroUserError('Failed to process currency. Please try again.');
        }
    }

    const buffNames = pkg.buffNames || [];
    console.log(`üíâ Applying ${buffNames.length} buff(s): ${buffNames.join(', ')}`);

    // Apply all buffs in parallel
    const buffCommands = buffNames.map(buffName =>
        takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
            command: `buffplayer "${player.name}" ${buffName}`
        })
            .then((response) => {
                const serverResponse = response.data.data?.rawResult || 'No response';
                console.log(`   ‚úì Applied ${buffName}`);
                console.log(`      Server response: ${serverResponse}`);
                return { success: true, buff: buffName };
            })
            .catch(err => {
                console.error(`   ‚ùå Failed to apply ${buffName}:`, err.message);
                if (err.response?.data?.data?.rawResult) {
                    console.error(`      Server response: ${err.response.data.data.rawResult}`);
                }
                return { success: false, buff: buffName };
            })
    );

    const results = await Promise.all(buffCommands);
    const successCount = results.filter(r => r && r.success).length;

    console.log(`‚úÖ Applied ${successCount}/${buffNames.length} buffs successfully`);

    // Set or update expiration variable if duration is set
    if (pkg.duration > 0) {
        const expiryKey = `buff_expiry_${pkg.commandName}`;
        const expiryTime = Date.now() + pkg.duration;  // ‚úÖ duration is already in milliseconds

        try {
            // Check if variable already exists
            const existingVars = await takaro.variable.variableControllerSearch({
                filters: {
                    key: [expiryKey],
                    playerId: [player.id],
                    gameServerId: [gameServerId],
                    moduleId: [module.moduleId]
                }
            });

            if (existingVars.data.data.length > 0) {
                // Update existing variable
                await takaro.variable.variableControllerUpdate(existingVars.data.data[0].id, {
                    value: expiryTime.toString()
                });
                const minutes = Math.floor(pkg.duration / 60000);
                const hours = Math.floor(minutes / 60);
                if (hours > 0) {
                    console.log(`‚è∞ Expiration updated: ${hours}h ${minutes % 60}m`);
                } else {
                    console.log(`‚è∞ Expiration updated: ${minutes}m`);
                }
            } else {
                // Create new variable
                await takaro.variable.variableControllerCreate({
                    key: expiryKey,
                    value: expiryTime.toString(),
                    playerId: player.id,
                    gameServerId: gameServerId,
                    moduleId: module.moduleId
                });
                const minutes = Math.floor(pkg.duration / 60000);
                const hours = Math.floor(minutes / 60);
                if (hours > 0) {
                    console.log(`‚è∞ Expiration set: ${hours}h ${minutes % 60}m`);
                } else {
                    console.log(`‚è∞ Expiration set: ${minutes}m`);
                }
            }
        } catch (err) {
            console.error(`‚ö†Ô∏è Failed to set expiration variable:`, err.message);
            // Don't throw - buff was applied successfully
        }
    } else {
        console.log(`‚è∞ No expiration (permanent buff)`);
    }

    // Send confirmation with properly formatted duration
    let expiryMsg = '';
    if (pkg.duration > 0) {
        const minutes = Math.floor(pkg.duration / 60000);
        const hours = Math.floor(minutes / 60);
        if (hours > 0) {
            expiryMsg = ` (expires in ${hours}h ${minutes % 60}m)`;
        } else {
            expiryMsg = ` (expires in ${minutes}m)`;
        }
    }

    await pog.pm(`${pkg.displayName} applied!${expiryMsg}`);
    console.log('‚úÖ Command complete');
}

await main();

