import { data, takaro } from '@takaro/helpers';

async function main() {
    const { gameServerId, eventData, player, pog, module } = data;

    if (!player) {
        console.log('‚ùå Error: Player data is undefined');
        return;
    }

    if (!pog) {
        console.log('‚ùå Error: Player-on-gameserver (pog) data is undefined');
        return;
    }

    console.log(`üîå Player connected: ${player.name || 'Unknown'} (ID: ${player.id})`);

    const config = module.userConfig;
    const buffPackages = config.buffPackages || [];

    if (buffPackages.length === 0) {
        console.log('‚ö†Ô∏è No buff packages configured');
        return;
    }

    // Filter packages that should auto-apply
    const autoApplyPackages = buffPackages.filter(pkg => pkg.autoApplyOnConnect === true);

    if (autoApplyPackages.length === 0) {
        console.log('‚ö†Ô∏è No packages with auto-apply enabled');
        return;
    }

    console.log(`üîç Checking ${autoApplyPackages.length} auto-apply package(s)`);

    const playerRoles = pog.roles || [];
    const playerRoleIds = playerRoles.map(r => r.role.id);

    console.log(`   Player has ${playerRoleIds.length} role(s)${playerRoleIds.length > 0 ? ': ' + playerRoleIds.join(', ') : ''}`);

    // Check each package for role requirements
    const eligiblePackages = autoApplyPackages.filter(pkg => {
        // If no role required, everyone is eligible
        if (!pkg.requiredRoleId || pkg.requiredRoleId.trim() === '') {
            console.log(`   ‚úì ${pkg.displayName}: No role required - eligible`);
            return true;
        }

        // Check if player has the required role
        const hasRole = playerRoleIds.includes(pkg.requiredRoleId);
        const icon = hasRole ? '‚úì' : '‚úó';
        console.log(`   ${icon} ${pkg.displayName}: ${hasRole ? 'HAS' : 'MISSING'} role ${pkg.requiredRoleId}`);
        return hasRole;
    });

    if (eligiblePackages.length === 0) {
        console.log('‚ö†Ô∏è Player is not eligible for any auto-apply packages');
        return;
    }

    console.log(`\nüíâ Applying ${eligiblePackages.length} buff package(s) to ${player.name}`);

    // Statistics
    const stats = {
        packagesApplied: 0,
        buffsTotalApplied: 0,
        packagesSkipped: 0,
        errors: 0
    };

    // Get existing buff variables to check what's already active
    let existingBuffVars = [];
    try {
        const varsRes = await takaro.variable.variableControllerSearch({
            filters: {
                playerId: [player.id],
                gameServerId: [gameServerId],
                moduleId: [module.moduleId]
            }
        });
        existingBuffVars = (varsRes.data.data || []).filter(v => v.key.startsWith('buff_expiry_'));
        console.log(`   Found ${existingBuffVars.length} existing buff variable(s)`);
    } catch (err) {
        console.log(`   ‚ö†Ô∏è Could not fetch existing variables: ${err.message}`);
    }

    // Apply each eligible package
    for (const pkg of eligiblePackages) {
        console.log(`\nüì¶ Processing package: ${pkg.displayName}`);

        try {
            // Check if this package is already active and not expired
            const expiryKey = `buff_expiry_${pkg.commandName}`;
            const existingVar = existingBuffVars.find(v => v.key === expiryKey);

            if (existingVar && pkg.duration > 0) {  // ‚úÖ Fixed: pkg.duration
                const expiryTime = parseInt(existingVar.value);
                const now = Date.now();
                const isStillActive = expiryTime > now;

                if (isStillActive) {
                    const msRemaining = expiryTime - now;
                    const minutesRemaining = Math.floor(msRemaining / 60000);
                    const hoursRemaining = Math.floor(msRemaining / 3600000);

                    const timeStr = hoursRemaining > 0
                        ? `${hoursRemaining}h ${minutesRemaining % 60}m`
                        : `${minutesRemaining}m`;

                    console.log(`   ‚è≠Ô∏è  Already active (${timeStr} remaining) - skipping`);
                    stats.packagesSkipped++;
                    continue;
                }
            }

            const buffNames = pkg.buffNames || [];
            if (buffNames.length === 0) {
                console.log('   ‚ö†Ô∏è No buffs configured - skipping');
                stats.packagesSkipped++;
                continue;
            }

            console.log(`   Buffs to apply: ${buffNames.join(', ')}`);

            // Apply all buffs in this package in parallel
            const buffCommands = buffNames.map(buffName =>
                takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
                    command: `buffplayer "${player.name}" ${buffName}`
                })
                    .then((response) => {
                        const serverResponse = response.data.data?.rawResult || 'No response';
                        console.log(`      ‚úì Applied ${buffName}`);
                        return { success: true };
                    })
                    .catch(error => {
                        console.log(`      ‚úó Failed to apply ${buffName}: ${error.message}`);
                        stats.errors++;
                        return { success: false };
                    })
            );

            const results = await Promise.all(buffCommands);
            const successCount = results.filter(r => r.success).length;

            console.log(`   ‚úÖ Applied ${successCount}/${buffNames.length} buffs`);

            if (successCount > 0) {
                stats.packagesApplied++;
                stats.buffsTotalApplied += successCount;
            }

            // Set or update expiration variable if duration is set
            if (pkg.duration > 0) {  // ‚úÖ Fixed: pkg.duration
                try {
                    const expiryTime = Date.now() + pkg.duration;  // ‚úÖ Fixed: duration is already in milliseconds

                    const minutes = Math.floor(pkg.duration / 60000);
                    const hours = Math.floor(minutes / 60);
                    const timeStr = hours > 0 ? `${hours}h ${minutes % 60}m` : `${minutes}m`;

                    if (existingVar) {
                        // Update existing variable
                        await takaro.variable.variableControllerUpdate(existingVar.id, {
                            value: expiryTime.toString()
                        });
                        console.log(`   ‚è∞ Expiration updated: ${timeStr}`);
                    } else {
                        // Create new variable
                        await takaro.variable.variableControllerCreate({
                            key: expiryKey,
                            value: expiryTime.toString(),
                            playerId: player.id,
                            gameServerId: gameServerId,
                            moduleId: module.moduleId
                        });
                        console.log(`   ‚è∞ Expiration set: ${timeStr}`);
                    }
                } catch (err) {
                    console.log(`   ‚ö†Ô∏è Failed to set/update expiration: ${err.message}`);
                    stats.errors++;
                }
            } else {
                console.log(`   ‚è∞ No expiration (permanent buff)`);
            }
        } catch (err) {
            console.log(`   ‚ùå Error processing package ${pkg.displayName}: ${err.message}`);
            stats.errors++;
        }
    }

    // Send welcome message if any buffs were applied
    if (stats.packagesApplied > 0) {
        try {
            const packageNames = eligiblePackages
                .filter(pkg => {
                    // Only include packages that weren't skipped
                    const expiryKey = `buff_expiry_${pkg.commandName}`;
                    const wasSkipped = existingBuffVars.find(v => v.key === expiryKey && parseInt(v.value) > Date.now());
                    return !wasSkipped;
                })
                .map(p => p.displayName)
                .join(', ');

            if (packageNames) {
                await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                    message: `Welcome ${player.name}! Auto-applied buffs: ${packageNames}`,
                    opts: {
                        recipient: {
                            gameId: pog.gameId
                        }
                    }
                });
                console.log('\n‚úÖ Welcome message sent');
            }
        } catch (error) {
            console.log(`\n‚ö†Ô∏è Failed to send welcome message: ${error.message}`);
        }
    }

    // Summary
    console.log('\n‚úÖ Auto-apply complete');
    console.log(`üìä Summary:`);
    console.log(`   - Packages applied: ${stats.packagesApplied}`);
    console.log(`   - Packages skipped (already active): ${stats.packagesSkipped}`);
    console.log(`   - Total buffs applied: ${stats.buffsTotalApplied}`);
    if (stats.errors > 0) {
        console.log(`   - Errors: ${stats.errors} ‚ö†Ô∏è`);
    }
}

await main();

cronJobs.function:
import { data, takaro } from '@takaro/helpers';

async function main() {
    const { gameServerId, module } = data;

    const config = module.userConfig;
    const buffPackages = config.buffPackages || [];

    console.log(`üîÑ Buff Expiration Check & Maintenance starting...`);

    if (!config.maintenanceEnabled) {
        console.log('‚è∏Ô∏è  Maintenance is DISABLED in config - skipping');
        return;
    }

    if (buffPackages.length === 0) {
        console.log('‚ö†Ô∏è No buff packages configured');
        return;
    }

    console.log(`   Configured packages: ${buffPackages.map(p => p.displayName).join(', ')}`);

    // Statistics tracking
    const stats = {
        playersProcessed: 0,
        buffPackagesMaintained: 0,
        buffPackagesExpiredAndRemoved: 0,
        totalBuffsReapplied: 0,
        errors: 0
    };

    try {
        // Get all online players
        const playersRes = await takaro.gameserver.gameServerControllerGetPlayers(gameServerId);
        const onlinePlayers = playersRes.data.data || playersRes.data || [];

        console.log(`\nüë• Found ${onlinePlayers.length} online player(s)`);

        if (onlinePlayers.length === 0) {
            console.log('   No players online - nothing to do');
            return;
        }

        // Get POG (PlayerOnGameServer) data for each online player
        const playerPogsPromises = onlinePlayers.map(async (onlinePlayer) => {
            try {
                const playerRes = await takaro.player.playerControllerSearch({
                    filters: {
                        steamId: [onlinePlayer.steamId]
                    },
                    limit: 1
                });

                if (!playerRes.data.data || playerRes.data.data.length === 0) {
                    console.log(`‚ö†Ô∏è  Player ${onlinePlayer.name} not found in Takaro database`);
                    return null;
                }

                const takaroPlayer = playerRes.data.data[0];
                const pogRes = await takaro.playerOnGameserver.playerOnGameServerControllerGetOne(
                    gameServerId,
                    takaroPlayer.id
                );

                return {
                    onlinePlayer,
                    takaroPlayer,
                    pog: pogRes.data.data
                };
            } catch (err) {
                console.error(`‚ùå Error fetching POG for ${onlinePlayer.name}:`, err.message);
                stats.errors++;
                return null;
            }
        });

        const playerPogsResults = await Promise.all(playerPogsPromises);
        const validPlayers = playerPogsResults.filter(result => result !== null);

        console.log(`\nüîç Processing ${validPlayers.length} player(s)...\n`);

        // Process each player
        for (const { onlinePlayer, takaroPlayer, pog } of validPlayers) {
            console.log(`\nüë§ Player: ${onlinePlayer.name}`);
            stats.playersProcessed++;

            try {
                const playerRoles = pog.roles || [];
                const playerRoleIds = playerRoles.map(r => r.role.id);
                console.log(`   Roles: ${playerRoleIds.length > 0 ? playerRoleIds.join(', ') : 'none'}`);

                // Get all buff expiration variables for this player
                console.log(`   üîç Searching for variables with filters:`);
                console.log(`      - playerId: ${takaroPlayer.id}`);
                console.log(`      - gameServerId: ${gameServerId}`);
                console.log(`      - moduleId: ${module.moduleId}`);

                const variablesRes = await takaro.variable.variableControllerSearch({
                    filters: {
                        playerId: [takaroPlayer.id],
                        gameServerId: [gameServerId],
                        moduleId: [module.moduleId]
                    }
                });

                const variables = variablesRes.data.data || [];
                console.log(`   üì¶ Total variables found: ${variables.length}`);
                if (variables.length > 0) {
                    console.log(`   üìù Variable keys: ${variables.map(v => v.key).join(', ')}`);
                }

                const expiryVariables = variables.filter(v => v.key.startsWith('buff_expiry_'));
                console.log(`   ‚è∞ Active buff tracking variables: ${expiryVariables.length}`);

                if (expiryVariables.length === 0) {
                    console.log(`   No active buffs to check/maintain`);
                    continue;
                }

                // Check each buff package variable
                for (const variable of expiryVariables) {
                    const commandName = variable.key.replace('buff_expiry_', '');
                    const pkg = buffPackages.find(p => p.commandName === commandName);

                    if (!pkg) {
                        console.log(`\n   ‚ö†Ô∏è  Package "${commandName}" no longer exists in config`);
                        console.log(`      üóëÔ∏è  Cleaning up orphaned variable`);
                        try {
                            await takaro.variable.variableControllerDelete(variable.id);
                            console.log(`      ‚úì Deleted orphaned variable`);
                        } catch (err) {
                            console.log(`      ‚ö†Ô∏è  Failed to delete: ${err.message}`);
                        }
                        continue;
                    }

                    console.log(`\n   üì¶ Package: ${pkg.displayName}`);

                    try {
                        // STEP 1: Check if expired
                        const expiryTime = parseInt(variable.value);
                        const now = Date.now();
                        const isExpired = pkg.duration > 0 && expiryTime > 0 && now > expiryTime;  // ‚úÖ Fixed: pkg.duration

                        // Debug logging
                        console.log(`      üìÖ Debug: Expiry=${new Date(expiryTime).toISOString()}, Now=${new Date(now).toISOString()}, Duration=${pkg.duration}ms`);
                        console.log(`      üîç Expired check: ${isExpired} (now ${now} > expiry ${expiryTime})`)

                        if (isExpired) {
                            const msExpired = now - expiryTime;
                            const minutesExpired = Math.floor(msExpired / 60000);
                            const hoursExpired = Math.floor(msExpired / 3600000);

                            const expiredTimeStr = hoursExpired > 0
                                ? `${hoursExpired}h ${minutesExpired % 60}m`
                                : `${minutesExpired}m`;

                            console.log(`      ‚è∞ EXPIRED ${expiredTimeStr} ago`);
                            console.log(`      üóëÔ∏è  Removing expired buffs from player`);

                            // Remove the actual buffs from the player
                            const buffNames = pkg.buffNames || [];
                            console.log(`      üíä Removing ${buffNames.length} buff(s): ${buffNames.join(', ')}`);

                            const debuffCommands = buffNames.map(buffName =>
                                takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
                                    command: `debuffplayer "${onlinePlayer.name}" ${buffName}`
                                })
                                    .then(() => {
                                        console.log(`         ‚úì Removed ${buffName}`);
                                        return { success: true };
                                    })
                                    .catch(err => {
                                        console.error(`         ‚ùå Failed to remove ${buffName}: ${err.message}`);
                                        return { success: false };
                                    })
                            );

                            const removeResults = await Promise.all(debuffCommands);
                            const removedCount = removeResults.filter(r => r.success).length;
                            console.log(`      ‚úÖ Removed ${removedCount}/${buffNames.length} buffs from player`);

                            // Send expiration notification to player
                            if (removedCount > 0) {
                                try {
                                    await takaro.gameserver.gameServerControllerSendMessage(gameServerId, {
                                        message: `‚è∞ ${pkg.displayName} has expired and been removed.`,
                                        opts: {
                                            recipient: {
                                                gameId: pog.gameId
                                            }
                                        }
                                    });
                                    console.log(`      üì¨ Expiration notification sent to player`);
                                } catch (err) {
                                    console.log(`      ‚ö†Ô∏è  Failed to send notification: ${err.message}`);
                                }
                            }

                            // Clean up expired variable
                            try {
                                await takaro.variable.variableControllerDelete(variable.id);
                                stats.buffPackagesExpiredAndRemoved++;
                                console.log(`      ‚úì Tracking variable deleted`);
                            } catch (err) {
                                console.log(`      ‚ö†Ô∏è  Failed to delete variable: ${err.message}`);
                                stats.errors++;
                            }
                            continue; // Don't re-apply expired buffs
                        }

                        // STEP 2: Check if player still has required role
                        if (pkg.requiredRoleId && pkg.requiredRoleId.trim() !== '') {
                            const hasRole = playerRoleIds.includes(pkg.requiredRoleId);

                            if (!hasRole) {
                                console.log(`      üîç Player no longer has required role`);
                                console.log(`      ‚è≠Ô∏è  Skipping (won't re-apply)`);
                                continue;
                            }
                        }

                        // STEP 3: Calculate time remaining (for active buffs)
                        if (pkg.duration > 0) {  // ‚úÖ Fixed: pkg.duration
                            const msRemaining = expiryTime - now;
                            const minutesRemaining = Math.floor(msRemaining / 60000);
                            const hoursRemaining = Math.floor(msRemaining / 3600000);

                            const remainingTimeStr = hoursRemaining > 0
                                ? `${hoursRemaining}h ${minutesRemaining % 60}m`
                                : `${minutesRemaining}m`;

                            console.log(`      ‚è∞ Active - Expires in ${remainingTimeStr}`);
                        } else {
                            console.log(`      ‚è∞ Active - Permanent (no expiration)`);
                        }

                        // STEP 4: Re-apply active buffs to maintain them
                        const buffNames = pkg.buffNames || [];
                        console.log(`      üíâ Re-applying ${buffNames.length} buff(s): ${buffNames.join(', ')}`);

                        const buffCommands = buffNames.map(buffName =>
                            takaro.gameserver.gameServerControllerExecuteCommand(gameServerId, {
                                command: `buffplayer "${onlinePlayer.name}" ${buffName}`
                            })
                                .then(() => {
                                    console.log(`         ‚úì Applied ${buffName}`);
                                    return { success: true };
                                })
                                .catch(err => {
                                    console.error(`         ‚ùå Failed ${buffName}: ${err.message}`);
                                    stats.errors++;
                                    return { success: false };
                                })
                        );

                        const results = await Promise.all(buffCommands);
                        const successCount = results.filter(r => r.success).length;

                        stats.totalBuffsReapplied += successCount;
                        stats.buffPackagesMaintained++;

                        console.log(`      ‚úÖ Maintained ${successCount}/${buffNames.length} buffs`);
                    } catch (err) {
                        console.error(`      ‚ùå Error processing package ${pkg.displayName}:`, err.message);
                        stats.errors++;
                    }
                }
            } catch (err) {
                console.error(`   ‚ùå Error processing player ${onlinePlayer.name}:`, err.message);
                stats.errors++;
            }
        }
    } catch (err) {
        console.error(`‚ùå Fatal error in buff check & maintenance:`, err.message);
        stats.errors++;
    }

    // Print summary
    console.log(`\n‚úÖ Buff Check & Maintenance Complete`);
    console.log(`üìä Summary:`);
    console.log(`   - Players processed: ${stats.playersProcessed}`);
    console.log(`   - Active buff packages maintained: ${stats.buffPackagesMaintained}`);
    console.log(`   - Expired buffs removed: ${stats.buffPackagesExpiredAndRemoved}`);
    console.log(`   - Total buffs re-applied: ${stats.totalBuffsReapplied}`);
    if (stats.errors > 0) {
        console.log(`   - Errors encountered: ${stats.errors} ‚ö†Ô∏è`);
    }
}

await main();
